    background-color: #90A4AE;
}


/* 500 */

.widget.__active .widget_cnt:hover, .toolbar, .toolbar_c, .toolbar_back, .toolbar_dropdown_w, .growl_close, .ic-online {
    background-color: #607D8B !important;
}


/* 600 */

.widget.__active .widget_cnt:focus:not(:active), .toolbar_back:hover, .toolbar_logo:hover, .toolbar_dropdown_w:hover, .toolbar_nav_a:hover, .growl_close:hover, .gift-badge_tx, .islands .online-fr_t {
    background-color: #546E7A !important;
}


/* 700 */

.toolbar_search .it_w, .widget.__active .widget_cnt:active, .toolbar_back:focus, .toolbar_back:active, .toolbar_dropdown_w:focus, .toolbar_dropdown_w:active, .toolbar_nav_a:focus, .toolbar_nav_a:active, .growl_close:focus, .growl_close:active {
    background-color: #455A64 !important;
}
.pf-with-ava .posting-form_itx_dec {
    border-color: #455A64;
}
.pf-with-ava .posting-form_itx_arw_w {
    border-color: #455A64 #455A64 transparent transparent;
}

/* 800 */
.al, #topPanelPopup_d a.wl.il.dsub_text.dsub_head_sub_leave {
    color: #37474F;
}
.circle-progress_cover {
    stroke: #37474F;
}
/* 900 */
.holiday_date.__today, .al:hover, .flash-link_w:hover .al.add-stub, .html5-link_w:hover .al.add-stub {
    color: #263238 !important;
}
/*Светлый фон - основной текст*/

/*.toolbar_dropdown .u-menu_li:first-child .u-menu_a,*/  .media-block.media-text, .widget_cnt, .widget_cnt:hover .widget_count, .widget_cnt:hover .widget_tx,
.search_suggest_t, .suggest .ucard_info_name {
    color: rgba(0, 0, 0, 0.87);
}

/*Светлый фон - вторичный текст*/

.filter_i, .feed_bб .feed_h, .feed-i_action, .ucard_info {
    color: rgba(0, 0, 0, .54) !important;
}


/*Светлый фон - вторичный текст - наведение*/

.filter_i:hover, .feed_b:hover, .feed_h:hover, .feed-i_action:hover {
    color: rgba(0, 0, 0, .58) !important;
}


/*Тёмный фон - основной текст*/

.widget.__active .widget_count, .widget.__active .widget_cnt:hover .widget_count, .widget.__active .widget_tx {
    color: white;
}


/* Фон карточки */

.feed, .growl, .notifs_cnt .notif, .search_suggest_block {
    background: #fff;
}
.growl:hover {
    background: #f7f7f7;
}
/* Вторичный фон карточки */

.__no-ava .feed_h:nth-child(2), .media_more:hover, .track:hover, [data-feed-type="127"] .feed_b,
.notifs_cnt .notif.__marked, .notif.__confirm, .notif.__removed {
    background: #f0f0f0;
}

/*Фон дравера*/
.toolbar_dropdown_w .u-menu {
    background: #fafafa !important;
    border-right: 1px solid #e0e0e0 !important;
}
.toolbar_dropdown .u-menu_li .u-menu_a {
    color: #546E7A;
}

.toolbar_dropdown .u-menu_li .u-menu_a:hover {
    background-color: #e0e0e0;
}


/* Стадии фона прозрачной кнопки */

.widget_cnt:hover {
    background-color: rgba(158, 158, 158, .2);
}

.widget_cnt:focus:not(:active) {
    background-color: rgba(0, 0, 0, .12);
}

.widget_cnt:active {
    background-color: rgba(158, 158, 158, .4);
}


/* Тень - 1px */

.feed {
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 1px -2px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .12);
}
/*Тень - 2px */
.search_suggest_block {
    box-shadow: 0 4px 5px 0 rgba(0,0,0,.14),0 1px 10px 0 rgba(0,0,0,.12),0 2px 4px -1px rgba(0,0,0,.2);
}

/* Контейнер фильтров */

.filter {
    border-bottom: 1px solid #e0e0e0;
    background: white;
}

.filter_i.__active, .islands-nav .mainContent_w .mctc_navMenuActiveSec, .islands-nav .mainContent_w .mctc_navMenuActiveSec:hover {
    color: rgba(0, 0, 0, .87) !important;
    border-bottom: 3px solid #37474F !important;
}
.islands .nav-side_i.__ac, .chats_i.__active {
    border-left-color: #37474F;
}

/* Границы карточки */

.feed_h, .feed-i_action, .media_more, .media-status_cnt, .toolbar_dropdown_w .u-menu_li.__divider,
.search_suggest_t {
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
}

.media_more {
    border-top: 1px solid rgba(0, 0, 0, 0.12);
}

#hook_Block_MainContent {
    background: #f2f0ed;
}

.islands #mainContent {
    width: 980px !important;
    border-radius: 2px;
    box-sizing: content-box;
}

.islands.user .ft-push, .islands.user #footer {
    width: 100%;
}

.user #mainContainer {
    padding-top: 55px !important;
    /*background-repeat-y: no-repeat;*/
    background-color: #f2f0ed;
}

#mainContainer {
    transition: background-image 0.8s ease 0.3s;
}

.islands.user .ft-push, .islands.user #footer {
    background: transparent;
}

.islands #hook_Block_MiddleColumnTopCard {
    border-radius: 2px;
}


#hook_Block_MainContent {
    background: transparent;
}

.islands .mctc_link {
    left: 15px;
}
/*Враппер колонки онлайна*/
.islands .fcofw .online-fr {
    z-index: 3;
}
/*Блок людей в онлайне*/
.islands.fcofw .online-fr_block {
    width: 79px;
    right: 0 !important;
    overflow: hidden;
    transition: 0.5s ease;
    transition-property: width, right;
}
/*Список людей*/
.islands.fcofw .online-fr_block .online-fr_list, .online-fr_city {
    width: 215px;
}
/*Разворачивание при наведении*/
.islands.fcofw .online-fr_block:hover {
    width: 215px;
    right: -110px !important;
}
/*Отступ от аватарки*/
.ucard-v.__h.__xxs .section {
    margin-right: 16px;
}
/*Сокрытие кнопок внизу*/
.online-fr_all, .online-fr_city {
    transition: opacity 0.3s ease 0.2s;
    opacity: 0;
}

.islands.fcofw .online-fr_block:hover .online-fr_all, .islands.fcofw .online-fr_block:hover .online-fr_city {
    opacity: 1;
}
