/*Text Color Orange*/
.error td,.entry,#filters,.update-note{color: #FF5700 !important}

/*Text Color Purple*/
.cm-s-default .cm-keyword {color: #CA00E6 !important}

/*-TextBox_Size-*/
.CodeMirror, .cm-s-default, .CodeMirror-wrap {height: 63% !important;}

