style.css:
@-moz-document url-prefix("http://www.bay12games.com/dwarves/") {
body   >   table:last-child   >   tbody   >   tr   >   td:first-child,   body   >   table:last-child   >   tbody   >   tr   >   td:last-child   {
            position:   relative;
            top:   -100px;
            left:   -100px;
}

body   >   table:last-child   >   tbody   >   tr   >   td:nth-child(2)   {
            width:   800px;
            position:   relative;
            left:   -100px;
}

body   >   table:last-child   >   tbody   >   tr   >   td:nth-child(2)   >   table   {
            width:   800px !important;
}
}
