style.css:
@-moz-document url-prefix("http://ask.fm/") {
html {background-color: #416B98 !important;}
body {margin: 65px 0 25px !important;}

a.link-news,#profile-button a.link-unfollow,.stream-answer a.link-unfollow,.popupPeopleBox a.link-unfollow,.flyout-people-buttons a.link-unfollow,.popupBlockBox-details a.link-unfollow,.signupFriends-userBox a.link-unfollow,#cb_consent_event_policy a.link-green,#profile-button a.link-green,.stream-answer a.link-green,.popupPeopleBox a.link-green,.flyout-people-buttons a.link-green,.signupFriends-userBox a.link-green,a.link-modern-left,.submit-button-more,a.signupButton,.logBox_sprite.logBox_submit,a.mobileAppBadge,a.link-login,.skinBox,.profileEditInput,a.profileEditControl,a.blueFriendsButton,a.notification-like,a.notification-question,a.notification-answer,.submit-button,#coverOptions .link-blue-underline,a.link-disableAccount,.register.submit-button-active,#registrationServiceBox>a>div,a.choiceAsk-logged,a.promptBtnBlue,a.promptBtnRight{
  transition: all .15s !important;
}
#wrapper,#footer,#footer_old,#footer_minimal,a.recommendedBoxLink,#profileEditFrame,#post_options,a.mobileAppBadge,a.btn.btn-block.btn-social,#register-form input,#register-form select{
  border-radius:0;
}
a.link-news,.yourLink,a.blueFriendsButton,a.profileEditControl-save,a.profileEditControl-cancel,span.qofday-label,.submit-button,a.link-disableAccount {
  border-radius: 2px;
}
a.link-flyout-section .border-none,.notification-profile,a#zoom_avatar:before,#profile-head-avatar-container a.livePictureEdit,#notification-like-digit,#notification-q-digit,#notification-answer-digit,.like-face,.askFriends-friend-head,#profile-picture,.stream-profile,body.body-welcome .head{
  border-radius: 50%;
}
#profileEditFrame,a.link-headline-active:last-child,a.link-headline:last-child,.recommendedBox_contour,a.recommendedBoxLink,a.blueFriendsButton,.submit-button,.skinBox-upload-active,.signinTile-top,#post_options,a.btn.btn-block.btn-social,#post_options-border{
  border: 0;
}
a.link-menu-active,a.link-menu-active:hover,body.body-welcome #loginTip,.submit-button,body.body-welcome #footer_old a.link-black,body.body-welcome #footer a.link-black{
  color: #fff;
}

#menu {background: #25486C !important;}
a.link-login {
  box-sizing: border-box !important;
  height: 42px !important;
  margin: 0 0 0 10px !important;
  line-height: 40px !important;
  padding: 0 10px !important;
  border-radius: 0 !important;
  font-size: 13px !important;
  text-transform: uppercase;
  background: 0 !important;
  border:0 !important;
}
a.link-login:hover {background-color: #305C8A !important;}
a.link-menu {
  position: relative !important;
  padding: 14px 8px !important;
  margin: 0 !important;
  transition: all .15s !important;
  overflow: hidden !important;
}
a.link-menu:after {
  content: '';
  display: block;
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  height: 3px;
  background: #8dc700;
  transition: all .15s;
}
a.link-menu:hover::after {margin-top: -3px;}
a.link-menu-active {border-bottom: 3px solid #8dc700;padding: 14px 6px 11px;color: #83bbd6;}
a.link-flyout-section:hover {background-color: #F3FFD8;}
a.link-flyout-section .flyout-picture {width: 34px;height: 34px;}
a.link-flyout-section .flyout-picture>img{width:100%;}
a.link-flyout-section .flyout-message {float: left;margin: 0 0 0 10px;}
a.recommendedBoxLink {background: #f0f7fa;}

.headline-menu {border-bottom: 2px solid #8dc700;margin: 0 auto 5px;}
a.link-headline {font-weight: 400;}
a.link-headline-active,a.link-headline {
  font-size: 24px;
  text-transform: uppercase;
  font-weight: 100;
  margin: 0;
  padding-right: 10px;
}
.link-headline-seperator {height: 25px;}

#logBox {top: 42px;right:-1px;width: auto;}
#logBox_top,#logBox_bottom {display: none;}
#logBox_content{background: #fff;padding: 15px;width: auto;}
#logBox_content .logBox_form {margin: 0;}
#leaderboard {display: none !important;}
#wrapper,#footer,#footer_old{box-shadow: 0 1px 3px rgba(103, 103, 103, 0.4);}
#wall_notice_container {margin: 10px 0 0;}

a.link-news {
  box-sizing: border-box;
  width: 300px;
  padding: 7px 15px;
  text-align: center;
  border: 1px solid #426A91;
  color: #426A91;
  font-size: 13px;
  background-color: #fff;
}
a.link-news:hover {color: #fff;background: #426A91;}

a.blueFriendsButton {padding: 8px 15px;background: #426A91;}
.blueFriendsButton.submit-button-active:hover {background: #638BB2;}
a.blueFriendsButton span {text-shadow: none;text-transform: uppercase;font-weight: 400;}
.submit-button {width: 145px;}

.reportProfileHolder{padding:5px 0 0;}
#profile-head-avatar-container a.livePictureEdit{
  background: rgba(0, 0, 0,.4) url(data:image/svg+xml;base64,) 50% no-repeat;
  background-size: 15px;
  top: 0;
  left: 0;
  width: 27px;
  height: 27px;
  transition: background .15s;
  border: 2px solid #fff;
}
#profile-head-avatar-container a.livePictureEdit:hover {background-color: rgba(0, 0, 0,.7);}
#profile-head {background-color: #fff;border-radius: 0;padding:20px 25px;}
a.link-profile-name {color: #4D769E;}
.stasis-digit {color: #4D769E;}
.stasis-label,.username {color: #414141;}
#liveProfileEdit_buttonHolder {height: 25px;}
a.liveProfileEdit {background: #5A80A9;color: #fff;height: 25px;}
a.liveProfileEdit:hover {background: #638BB2;}
a.liveProfileEdit span {padding: 5px 15px 6px;}
.live-edit-sprite {background-image: none;}
#liveProfileEditBox_top,#liveProfileEditBox_bottom {display: none;}
#liveProfileEditBox_content {background: #fff;box-shadow: 0 2px 4px #444;padding: 9px 20px 10px;}
#profileEditFrame {background: transparent;}
.profileEditTile {padding: 10px 0px 12px;background: none;}
.profileEditInput{
  top: 3px;
  left: 0;
  border-bottom: 2px solid #D0D0D0;
  padding: 7px 0px 7px;
  color: #888;
  width: 245px;
}
.profileEditInput:focus,.profileEditTextarea:focus {border-bottom: 2px solid #8dc700;color: #222;}
.profileEditTileMask {background: #fff;}
.profileTextareaDemo {left: 0px;top: 16px;}
.profileEditTextarea {
  border: 0 solid #fff;
  border-bottom: 2px solid #D0D0D0;
  width: 231px;
  padding: 5px 7px 3px 0;
  color: #888;
}
a.profileEditControl {text-shadow: none;}
a.profileEditControl-save {background: #8dc700;}
a.profileEditControl-save:hover {background: #E88C24;}
a.profileEditControl-cancel {background: #DDD;}

input[type="submit"].submit-button,#wrapper-upload-container input[type="button"]:first-child,#login-buttonContainer input[type="button"],#contact_us_submit {background: #8dc700;color: #fff;}
input[type="submit"].submit-button:hover, #wrapper-upload-container input[type="button"]:first-child:hover,#change_picture:hover {background:#E88C24;color:#fff;}
input[type="button"].submit-button,input[type="reset"].submit-button,#login-buttonContainer input[type="button"]:nth-child(2) {background: #ddd;color: #414141;}

#notification-like-digit, #notification-q-digit, #notification-answer-digit {
  background-color: #DF5757;
  padding: 0 3px;
  font-weight: 400;
  min-width: 9px;
  min-height: 12px;
  border: 1px solid #fff;
  border-radius: 10px;
}

#profile-button a.link-unfollow, .stream-answer a.link-unfollow, .popupPeopleBox a.link-unfollow, .flyout-people-buttons a.link-unfollow, .popupBlockBox-details a.link-unfollow, .signupFriends-userBox a.link-unfollow {
  padding: 6px 10px;
  border: 0;
  background: #EFEFEF;
  text-shadow: none;
  border-radius: 2px;
}
/*Delete buttons*/
#profile-button a.link-unfollow:hover, .stream-answer a.link-unfollow:hover, .popupPeopleBox a.link-unfollow:hover, .flyout-people-buttons a.link-unfollow:hover, .popupBlockBox-details a.link-unfollow:hover, .signupFriends-userBox a.link-unfollow:hover,a.profileEditControl-cancel:hover,input[type="button"].submit-button:hover,#remove_picture:hover,a.link-disableAccount:hover,#login-buttonContainer input[type="button"]:nth-child(2):hover,input[type="reset"].submit-button:hover {
  background: #EC5654;
  color: #FFF;
  text-shadow: none;
}
#cb_consent_event_policy a.link-green, #profile-button a.link-green, .stream-answer a.link-green, .popupPeopleBox a.link-green, .flyout-people-buttons a.link-green, .signupFriends-userBox a.link-green {
  padding: 6px 10px;
  text-shadow: none;
  border: 0;
  border-radius: 2px;
  background: #8dc700;
}
#cb_consent_event_policy a.link-green:hover, #profile-button a.link-green:hover, .stream-answer a.link-green:hover, .popupPeopleBox a.link-green:hover, .flyout-people-buttons a.link-green:hover, .signupFriends-userBox a.link-green:hover {
  background: #E88C24;
}
a.link-modern-left {
  border: 0;
  border-radius: 2px;
  background: #5A80A9;
  color: #fff;
  padding: 6px 10px;
}
a.link-modern-left:hover {background: #2D5C8E;color: #fff;}
a.btn-share {
  position: absolute;
  bottom: -15px;
  border-radius: 50%;
  -moz-border-radius: 50%;
  -webkit-border-radius: 50%;
}
a.btn-share.link-edit{right:105px;}
a.btn-share.link-share{right:65px;}
a.btn-share.link-makeGift{right:25px;}

#profile-info,#profileHead_floatHolder {position: static;}
#profile-info {min-height: 95px;}

#profile-input, #post-input-pre {width: 671px;max-width: 671px;border: 2px solid #D0D0D0;border-radius: 0;}
#profile-input:focus,#post-input-pre:focus,#popup-content .input-message:focus,#popup-content .input-smallLine:focus,#popup-ask-textarea:focus,.registrationLevel input:focus,.registrationLevel textarea:focus,.registrationLevel select:focus{border: 2px solid #8dc700;}
#post_options-border {width: 684px;}
#post_options {width: 684px;background: #fff;padding: 8px 0px 4px;}
.submitBlue,a.promptBtnBlue,a.promptBtnRight {
  background-image:none;
  background: #5A80A9;
  border: 0;
  border-radius: 2px;
  padding: 5px 17px;
  text-shadow:none;
}
a.promptBtnRight{background:#ddd;}
a.promptBtnBlue,a.promptBtnRight{padding: 10px 20px;}
.submitBlue.submit-button-active:hover,a.promptBtnBlue:hover {background: #638BB2;}

.infoBox {border-bottom: 2px solid #8dc700;}
.infoBox .text-headline {font-weight: 100;text-transform: uppercase;}

#cboxOverlay {opacity: .5 !important;background: #000;}
#cboxContent {background: transparent;}
#cboxTitle {background: rgba(0, 0, 0, 0.6);}
#cboxLoadedContent{background:#fff;}
#cboxBottomLeft,#cboxBottomCenter,#cboxBottomRight,#cboxMiddleLeft,#cboxMiddleRight,#cboxTopLeft,#cboxTopCenter,#cboxTopRight {display: none;}
#cboxLoadingOverlay {background:#fff !important;}

#searchBox {border-radius:0 !important;background: #fff !important;border: 0 !important;}
#searchBox-formFrame {position:relative !important;border: 2px solid #D0D0D0 !important;background: 0 !important;width: 651px !important; padding:10px 15px 11px !important;}
#searchBox-formFrame .searchBox-submit {
  background: #D0D0D0;
  color: #FFF !important;
  text-shadow: none !important;
  border: 0 !important;
  border-radius: 0 !important;
  top: -2px !important;
  right: -2px !important;
  height: 43px !important;
  padding: 0 15px !important;
}
.searchBox-submit:hover,a.promptBtnRight:hover {
  color: #fff !important;
  background: #8dc700 !important;
}
#searchBox .searchBox-separator:first-child{display: none !important;}
#searchBox .searchBox-separator{height: 1px !important;background: #EBEBEB !important;}
.searchBox-socialLabel {text-shadow:none !important;}
#searchBox a.searchBox-socialService:hover {background: #EBEBEB;}

#shareLinkReminder {
  border: 0;
  border-radius: 0;
  background: #f0f7fa;
}
.yourLink {
  background: #fff;
  color: #426A91;
  border: 1px solid #426A91;
}
a.closeBlueBox {top: 5px;right: 5px;}

.submit-button-more {
  padding: 8px 0;
  border:0;
  border-radius: 2px;
  color: #fff;
  background: #426A91;
}
.submit-button-more.submit-button-active:hover {background: #638BB2; color: #fff;}

#logBox,.flyout-ask_follow {border-radius: 0;border: 0;box-shadow: 0 2px 3px rgba(0, 0, 0, 0.36);}
img.photoAnswer-XL {border: 0;padding: 0;}
a.photoAnswer-zoom:hover img.photoAnswer-XL,img.video-preview:hover {border: 0;opacity: .8;}

#popup-ask-textarea {border-radius: 0;border: 2px solid #ccc;}
.questionPop-text .text-bold {font-weight: 400;}

.askFriends-friend-checked, .askFriends-friend {border-radius: 0;}
.askFriends-friend-checked {background: #5A80A9;}

span.qofday-label {
  text-transform: uppercase;
  border: 1px solid #426A91;
  color: #426A91;
  padding: 4px 9px;
}
.qofday-icon {margin: 0 1px 0 5px;}

#captchaLevel {border:0;background:#FFF;margin:0 auto 5px;padding:4px 0;width:300px;}
#ask_captcha_content input.field-captcha {border:2px solid #D0D0D0;}
#giftCategory {border:0;background:#fff;padding: 13px 16px 0;height: 33px !important;}
#giftCategory .giftCategory-list,#giftPop-box {border:2px solid #D0D0D0;border-radius:0;}
#gift_select_border {background:#fff;}
#giftPop-box .giftItem,#giftPop-box .giftItem-selected,#gift_select_willpaginate_container>a {border-radius:0 !important;}
#gift_select_willpaginate_container>a{padding:5px 9px 4px;}
#gift_select_willpaginate_container>a.link-page,#gift_select_willpaginate_container>a.link-page-active{margin: 0;}

#flyout-like, #flyout-answer {margin: 0;border: 0;overflow: visible;}
#flyout-replies-wrapper, #flyout-wrapper{
  padding: 0;
  background: #fff;
  box-shadow: 0 2px 7px rgba(0, 0, 0, 0.44);
  overflow: visible;
  top: 42px;
}
#flyout-wrapper {left: 126px;}
#flyout-replies-wrapper {left: 190px;}
#flyout-answer:before, #flyout-like:before {
  position: absolute;
  top: -11px;
  content: '';
  display: block;
  width: 22px;
  height: 11px;
  background: #fff;
}
.flyoutHeadline {
  font-weight: 400;
  text-transform: uppercase;
  color: #426A91;
  width: 302px;
  box-sizing: border-box;
  margin: 0;
  padding: 8px 10px 5px;
}
.registrationLevel input:disabled {color: #999;}

a.signupButton,.register.submit-button-active {
  background: #85BE3A;
  text-shadow: none;
  border-radius: 2px;
  border: 0;
  box-shadow: none;
  text-transform: uppercase;
}
a.signupButton:hover,.register.submit-button-active:hover {background: #6EAD1D;}
.signinFrame {
  border: 0;
  border-radius: 0;
  background: transparent;
}
.signinInput {
  border-bottom: solid 2px #ccc;
  color: #414141;
}
.signinTileMask {background: #fff;}
.signinFrame input:focus,.signinFrame select:focus,.signinFrame textarea:focus,.signinFrame object {
  border-bottom: 2px solid #85BE3A;
}
.logBox_sprite.logBox_submit {text-shadow:none !important;background: #85BE3A !important;border-radius: 2px;}
.logBox_sprite.logBox_submit:hover {background: #6EAD1D !important;}
#logBox_submit-spinner {background: #9ED139 !important;}
#heads,#language,#whosHere-label {background: transparent;}
#screen {background: #416B98;}
#whosHere-label,#signupSlogan {font-weight: 100;}
#heads {
  margin: 0 auto;
  padding: 20px 0 0 15px;
  width: 720px;
  border-top: 2px dashed #305C8A;
  height: 52px;
}
#language {
  width: 720px;
  margin: 0 auto 5px;
  padding: 30px 0 10px 15px;
  border: 0;
  color: #fff;
}
#language .text-black {color: #fff;}
#language a.link-blue {color: #9EC1E5;}
a.mobileAppBadge {background: #305C8A;}

a.link-viewAllNotifications {
  color: #426A91;
  border-top: solid 1px #91CC03;
  width: 282px;
  transition: all .15s;
}
a.link-viewAllNotifications:hover { color: #FFF; background-color: #91CC03; }

.sharing-and-likers{position: absolute; right: 28px; padding: 0;opacity:.65;}
#common_question_container.container-profile-promo .sharing-and-likers{position:static;padding:10px 0 0;opacity:1;}
.questionBox:hover .sharing-and-likers {opacity: 1;}
.sharing-and-likers .n-footer_social_icon{margin: 0;}

.questionBox .text-headline { font-weight: 100; text-transform: uppercase;}
.container-profile .questionBox .question{width: 570px;}
a.delete,a.delete-blue{font: normal normal normal 14px/1 FontAwesome;background: 0 0;}
a.delete-blue:before,a.delete.hintable:before {
  box-sizing: border-box;
  display: block;
  content: "\f00d";
  position: absolute;
  padding: 5px 6px;
  font-size: 12px;
  color: #CBCBCB;
  width: 24px;
  height: 24px;
  border: 1px solid #E9E9E9;
  border-radius: 50%;
  right: 0;
}
a.delete-blue:before{right:10px;color:#bbb;border-color:#d5d5d5;}
#common_question_container.container-profile-promo a.delete.hintable:before{right:25px;}
.container-wide .questionBox a.delete.hintable:before,.container-wide .questionBox-daily a.delete.hintable:before{right:20px;}
a.delete-blue:hover:before,a.delete.hintable:hover:before {
  color: #fff;
  background: #DF5757;
  border-color: #DF5757;
}
.askFriends-friend-head {width: 63px;height: 63px;}
.askFriends-friend-details {width: 102px;}
.skinBox:hover { border: 2px solid #fff; opacity: .8; }

.questionBox-sponsored {background-color: #FFEEC4;border-radius: 0;}
a.ticket {background: 0;}
a.ticket:hover {background: 0;color: #885300;}
a.ticket span{background: #FFDA89;padding: 2px 10px;}
a.ticket span:hover {background: #FFB23A;}

#popup-content .input-message, #popup-content .input-smallLine, #askFriends-wrapper,.registrationLevel .Form_select,.registrationLevel .Form_text,.registrationLevel .Form_text-multi {border: 2px solid #D0D0D0;border-radius: 0;}
#popup-content .stream-profile { width: 40px; height: 40px; }
#popup-content .popupPeopleBox-details {min-height: 40px;}

#coverOptions .link-blue-underline {
  display: block;
  height: 40px;
  text-align: center;
  text-decoration: none;
  font-size: 13px;
}
#change_picture {line-height: 42px;background: #8dc700;color: #fff;}
#remove_picture {line-height: 37px;background: #DDD;color: #414141;}
a.link-disableAccount {
  background: #DDD;
  height: 27px;
  line-height: 26px;
  margin: 0;
  text-decoration: none;
  padding: 0 25px;
  color: #444;
}
.registrationLevel input:disabled{color: #A8A8A8;background: #DDD;}
a.choiceAsk-logged {background: #5A80A9; color: #fff;text-shadow: none;}
a.choiceAsk-logged:hover {background: #8dc700;color: #fff;}

a.serveFacebook,.serveFacebook-fill,a.serveTwitter,.serveTwitter-fill,a.serveVk,.serveVk-fill {border:0;-webkit-box-shadow:none;box-shadow:none;border-radius:0;}
.serveFacebook-fill h1,.serveTwitter-fill h1,.serveVk-fill h1 {background: 0;text-shadow: none;padding: 8px 0;}
.serveFacebook-fill,a.logBox_service-facebook:before {background:#4C68A1;}.serveFacebook-fill:hover{background:#6F86B3;}
.serveTwitter-fill,a.logBox_service-twitter:before{background:#55acee;}.serveTwitter-fill:hover{background:#77BCF1;}
.serveVk-fill,a.logBox_service-vkontakte:before{background:#597DA3;}.serveVk-fill:hover{background:#6289ba;}
a.logBox_service-facebook:hover:before,a.logBox_service-twitter:hover:before,a.logBox_service-vkontakte:hover:before{opacity:.8;}
a.logBox_service.logBox_sprite {background: 0 0;margin: 0 5px 0 0;}
a.logBox_service-facebook:before,a.logBox_service-twitter:before,a.logBox_service-vkontakte:before {
  display: block;
  width: 42px;
  height: 40px;
  position: absolute;
  font: normal normal normal 22px/1 FontAwesome;
  line-height: 40px;
  color: #fff;
  border-radius: 50%;
}
a.logBox_service-facebook:before {content:"\f09a";}
a.logBox_service-twitter:before  {content:"\f099";}
a.logBox_service-vkontakte:before{content:"\f189";}

body.body-welcome .head,.askFriends-friend-head {border: 2px solid #fff;}
body.body-welcome #menu {background: #416B98 !important;border-bottom: 2px solid #305C8A;}
body.body-welcome #footer{border-top: 2px solid #305C8A;}
body.body-welcome #footer_old .footerItem {border-color: #E2E2E2;}
body.body-welcome #footer_old, body.body-welcome #footer {background:transparent; color:#fff; box-shadow:none;}
body.body-welcome #footer_copyright {color: #fff;}
#footer_old {padding: 10px 25px;}
#footer_old .footerItem {border-color: #818181;}
}
