style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("savepic.ru"), domain("savepic.org") {
    
  body, html  {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;background-attachment: fixed !important;background-clip: border-box !important;background-origin: padding-box !important;background-position: center center !important;background-repeat: no-repeat !important;background-size: cover !important; font-family:Constantia!important}
   h2, p.msg, .b-links-help, a.button:link, a.button:active, a.button:hover, a.button:visited, #bonus>p>input, .subm {background-color: #000 !important; background: linear-gradient(to bottom, #000 0%, #333 100%) !important}
  .b-upload-img, .b-miniatura, .b-gallery table{background: rgba(0, 0, 0,  0.6)!important; box-shadow: 0px 0px 9px rgba(255, 0, 0,  .9 ) !important}
  #bonus>p>input, textarea, #annot>fieldset>p>select, #bonus>fieldset>dl>dt>label>select, .subm:hover {background: linear-gradient(to bottom, #000 0%, #333 100%) !important; box-shadow: 0px 0px 3px rgba(255, 0, 0,  .9 )  !important; border: none !important}
  .b-col .b-col__l, .b-col .b-col__r, .b-links-help, a.button:link, a.button:active, a.button:hover, a.button:visited, .b-gallery td, .b-gallery, .subm  {border: none !important}
  .b-gallery td {background: transparent !important}
  .l-header, .l-footer {background: rgba(0, 0, 0,  0.7)!important;}
  .b-upload-img fieldset {border-color: red !important}
  .l-body, a, h1 {color: #33ccee !important; text-decoration: none !important}
   a:hover, p {color: #3399ff !important; text-decoration: none !important}}
