style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vnfoss.blogspot.com") {
  #outer-wrapper {
    font-size: 16px;
    font-family: "Liberation Serif", "Times New Roman", serif !important;
    
    margin-left: auto;
    margin-right: auto;
    width: 70em;
  }
  
  .post-body br {
    display: none;
  }

  .post-body > div {
    line-height: 1.4em !important;
    margin-bottom: 1em !important;
  }

  .post-body img + br {
    display: block !important;
  }

  .post-body span[style*="background: #ffff00;"] {
    background: #FFFAC4 !important;
    display: block;
  }
  
  .post-body span[style*="color: red;"] {
    color: #FF5A4C !important;
  }
  
  .sidebar {
    font-family: "Liberation Sans", "Arial", sans-serif;
    font-size: 0.9em;
  }
  
  .widget.TextList li {
    text-indent: 0 !important;
    padding-left: 0 !important;
    padding-top: 1em;
    padding-bottom: 1em;
    border-top: 1px dashed #BBB;
  }

}
