style.css:
@-moz-document domain("google.com") {

/* the logo is gone */

#hplogo, #gbqlw img{ 	
	height: 0 !important;
	width: 0 !important;
	display: none !important;
}

#logo{
	display: none !important;
}

/* holiday icons are gone */

#prm-pt, #prm{
    display: none !important;
}

/* the header bar is gone. i couldn't make it display:none without messing up the margins, though */
#gb{
	visibility: hidden;
}

/* the footer bar is gone */
._dQc._E2{
	display: none;
}

/* the google plus stuff is gone */
.gb_Kc.gb_0c.gb_n, .gb_ea.gb_0c.gb_n.gb_Zc.gb_p{
	display: none;   
}

/* the remaining upper right options are gone */
.gb_ea.gb_0c.gb_n.gb_Zc.gb_3c.gb_p{
    display:none; /* comment this line and uncomment the next if you want the +Name, Gmail, and Images options in the top right to still be visible */
    /*padding: 0px;*/
    
}

/* the search and lucky buttons are gone, even inside the search suggestion box */
.jsb, .sbsb_g{
	display:none !important;
}

/* the "say ok google" text is gone */
input#gs_htif0.gsfi 
{
    -webkit-text-fill-color: white !important;
    color: white !important;
}
/* the microphone icon is gone */
#gsri_ok0, .gsst_b.sbib_c{
    display: none !important;
}
}
