style.css:
/* Easy On The Eye-conosquare */
/* Sloppy style by Brndn */

@-moz-document
	domain(iconosquare.com) {
	html, body, .snap-content, ads-footer, .page-content-wrapper, .footer {
		background-color:#111;
	}
		.thumbnail, .feed {
			background-color:#444;
			border:10px solid #444;
		}
	.thumb-wrapper .user-name {
		color:#FAFAFA;
	}
		.footer {
			margin-top:-50px;
			border-top:0px;
		}
		.page-header{
		padding:40px 0 0 0;
	}
		.profile-nav, .sticky-header, .desktop-navigation {
			background-color:#454545;
		}
}
