style.css:
@-moz-document domain("youtube.com") {
  body {
    background: url("http://www.savasport.com/wp-content/uploads/2013/12/dark-gray.jpg") !important;
  }
  .page-loaded #page {
    background: url("http://www.savasport.com/wp-content/uploads/2013/12/dark-gray.jpg") !important;
  }
  
  .yt-card {
    background: rgba(0, 0, 0, 0.6);
    border-radius: 5px;
  }
  
  .branded-page-module-title-text {
    color: #E6E6E6;
  }
  
  .branded-page-module-title-text:hover {
    color: #CC181E;
  }
  
  .yt-ui-ellipsis {
    background: transparent;
  }
  
  .yt-lockup-title a {
    color: #DFDFDF;
  }
  
  .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a, .multirow-shelf .yt-uix-expander-head, #footer-links-primary a:hover, #footer-links-secondary a:hover, a, a:hover, .comments .comment-header .user-name, .comments .like-count, .comments .comment-replies-header .load-comments a, .comments .comment-replies-header .hide-comments a, .video-player-view-component .video-detail a {
    color: #CC181E;
  }
  
  .yt-lockup, .yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
    color: #A9A9A9;
  }
  
  .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
    border-bottom: 1px solid #3C3C3C;
  }
  
  #masthead-appbar, #yt-masthead-container, #appbar-guide-menu {
    background: rgba(0, 0, 0, 0.6);
    border: none;
  }
  
  .guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: none;
  }
  
  #masthead-appbar {
    position: relative;
    margin-left: auto;
    width: calc(100% - 230px);
  }
  
  .epic-nav-item-heading {
    color: #E6E6E6;
  }
  
  #appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
    color: #BABABA;
    box-shadow: none;
  }
 
  .guide-item {
    color: #BABABA;
  }
  
  .guide-view-more {
    color: #9C9C9C;
  }
  
  .guide-item:hover, .guide-view-more:hover {
    background: rgba(0, 0, 0, 0.5);
  }
  
  .guide-item.guide-item-selected, #creator-sidebar .creator-sidebar-item.selected {
    background-image: linear-gradient(#CC181E, #151515);
    border: 1px solid #000;
    color: #BFBFBF;
  }
  
  .guide-item.guide-item-selected:hover, #creator-sidebar .creator-sidebar-item.selected:hover {
    background-image: linear-gradient(#CC181E, #151515);
    border: 1px solid #000;
    color: #FFF;
  }
  
  .yt-uix-button-text, .yt-uix-button-text[disabled] {
    background: transparent !important;
    border: none !important;
  }
  
  #yt-masthead #logo-container .logo, #footer-logo .footer-logo-icon {
    background: url("http://i.imgur.com/kJMnznw.png?2") no-repeat;
    background-size: 72px;
    width: 72px;
    height: 30px;
  }
  
  #yt-masthead #logo-container span.content-region {
    color: #BABABA;
  }
  
  body #footer-container {
    background: rgba(0, 0, 0, 0.5);
    border: none;    
  }
  
  #footer-main, #footer-links {
    text-align: center;
  }
  
  #footer-links-primary a {
    color: #E6E6E6;
  }
  
  #footer-links-secondary a, #footer-links-secondary .copyright {
    color: #BABABA;
  }
  
  .yt-uix-button, .compact-shelf .yt-uix-button-shelf-slider-pager {
    border-radius: 5px;
    border: 1px solid #000;
    background-image: linear-gradient(#CC181E, #151515);
    color: #BFBFBF;
    box-shadow: 0px 0px 4px #CC181E;
  }
  
  .yt-uix-button:hover {
    border-radius: 5px;
    border: 1px solid #000;;
    background-image: linear-gradient(#CC181E, #151515);
    color: #FFF;
    box-shadow: 0px 0px 4px #CC181E;
  }
  
  .yt-uix-button:focus, .yt-uix-button:active {
    box-shadow: none;
    background-image: linear-gradient(#CC181E, #151515);
    border: 1px solid #000;
  }
  
  .yt-uix-button-default.yt-uix-button-toggled, .yt-uix-button-default.yt-uix-button-toggled:hover {
    box-shadow: none;
    background-image: linear-gradient(#CC181E, #151515);
    border: 1px solid #000;
  }
  
  .compact-shelf .yt-uix-button-shelf-slider-pager {
    background: #000;
  }
  
  #masthead-search-terms {
    background: #000;
  }
  
  .masthead-search-terms-border {
    border: none;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    box-shadow: inset 1px 1px 4px #CC181E, 0px 0px 4px #CC181E;
  }
  
  #masthead-search-terms input {
    color: #FFF;
  }
  
  .yt-subscription-button-subscriber-count-branded-horizontal {
    box-shadow: 0px 0px 4px #CC181E;
  }
  
  .branded-page-module-title a:visited, .branded-page-module-title a {
    color: #FFF;
  }
  
  .yt-uix-menu-content {
    background: rgba(0, 0, 0, 0.5);
    box-shadow: 0px 0px 4px #CC181E;
    border: 1px solid #000;
    border-radius: 5px;
  }
  
  .yt-ui-menu-item {
    color: #BABABA;
  }
  
  .yt-ui-menu-item:hover {
    color: #FFF;
    background: transparent;
  }
  
  #yt-picker-country-footer, #yt-picker-language-footer, #yt-picker-safetymode-footer {
    background: rgba(0, 0, 0, 0.5);
  }
  
  .yt-default h1, .yt-default h2, .yt-default h3, .yt-default h4, .yt-default h5, .yt-default h6, h1.yt, h2.yt, h3.yt, h4.yt, h5.yt, h6.yt {
    color: #BABABA;
  }
  
  p.yt-notes {
    color: #8E8E8E;
  }
  
  .yt-picker-header {
    border-bottom: 1px solid #585858;
  }
  
  #safety-form p.safety-submit {
    border-top: 1px solid #585858;
  }
  
  a.yt-uix-button.yt-uix-button-epic-nav-item {
    box-shadow: none;
    color: #8E8E8E;
  }
  
  .yt-uix-clickcard-card-border {
    background: #000;
    color: #BABABA;
    box-shadow: 0px 0px 4px #CC181E;
    border-color: #CC181E;
  }
  
  .yt-masthead-picker-name, a.yt-masthead-picker-active-account {
    color: #BABABA;
  }
  
  .yt-masthead-picker-footer {
    padding: 10px 15px;
    border-top: 1px solid #CC181E;
    background: none repeat scroll 0% 0% #000;
    overflow: hidden;
  }
  
  #creator-page-sidebar, .ytg-fl {
    box-shadow: 0px 0px 4px #CC181E;
  }
  
  .account-container {
    background: rgba(0, 0, 0, 0.5);
  }
  
  .account-header h2, h3.account-section-header, .account-email, .account-info, .account-info-item .account-info-label, .video-list .video-list-item .title {
    color: #BABABA;
  }
  
  #creator-sidebar .creator-sidebar-section a, #watch7-sidebar .watch-sidebar-head {
    color: #A6A6A6;   
  }
  
  #creator-sidebar .creator-sidebar-section a:hover {
    background: rgba(0, 0, 0, 0.5);
    color: #A6A6A6; 
  }
  
  #watch8-action-buttons .yt-uix-button {
    box-shadow: none;
  }
  
  .video-list-item .title {
    color: #CC181E;
  }
  
  .video-list-item a:hover ~ .video-list .video-list-item .title {
    color: #CC181E;
  }
  
  .comments .channel-owner .comment-header .user-name {
    background-color: transparent;
  }
  
  .comment-text-content, .comments .comment-header .comment-source {
    color: #BABABA;
  }
  
  .comments .comment-item .mod-button .default-state.sprite_like, .comments .comment-item .mod-button .default-state.sprite_dislike {
    opacity: 1;
  }
  
  .watch-time-text, #watch-header .yt-user-info a, .branded-page-base-bold-titles .channel-header .branded-page-header-title .branded-page-header-title-link, .video-player-view-component .view-count .content-item-time-created, .video-player-view-component .view-count .count, .view-as-menu-text, .primary-header-contents .view-as-menu .view-as-menu-trigger, .branded-page-base-bold-titles .branded-page-module-title, .branded-page-related-channels h3 a, .secondary-header-contents .nav-text {
    color: #BABABA;
  }
  
  #eow-description, .watch-extras-section .title, .watch-info-tag-list li, .c4-checklist-module .todo-title-text, .feed-item-actions-line, .feed-item-channel-rec-text a {
    color: #A6A6A6;
  }
  
  #action-panel-details a {
    color: #FFF;
  }
  
  .video-player-view-component .description {
    background: transparent;
    color: #A6A6A6;
  }
  
  .yt-ui-menu-item:focus {
    background: rgba(0, 0, 0, 0.5);
  }
  
  #channel-feed-post-form textarea, .yt-uix-form-input-select, .yt-uix-form-input-checkbox + .yt-uix-form-input-checkbox-element, .yt-uix-form-input-radio + .yt-uix-form-input-radio-element, .yt-uix-form-input-text, .yt-uix-form-input-textarea {
    color: #FFF;
    background: #000;
    border: none;
    text-shadow: none;
  }
  
  #channel-feed-post-form textarea:focus, .yt-uix-form-input-select.focused, .yt-uix-form-input-checkbox:focus + .yt-uix-form-input-checkbox-element, .yt-uix-form-input-radio:focus + .yt-uix-form-input-radio-element, .yt-uix-form-input-text:focus, .yt-uix-form-input-textarea:focus {
    box-shadow: 0px 0px 5px #CC181E;
    border: 1px solid #CC181E;
  }
  
  .branded-page-v2-has-top-row .branded-page-v2-secondary-col .branded-page-box:hover {
    background: transparent;
  }
  
  #other-channels-sidebar:hover {
    background: rgba(0, 0, 0, 0.6);
  }
  
  .guide-flyout {
    background: rgba(0, 0, 0, 0.5);
    border-color: #000;
  }
  
  .feed-item-content-wrapper.playlist-promo, .feed-item-content-wrapper.channel-lockup {
    border: 1px solid #000;
    box-shadow: 0px 0px 2px #CC181E;
  }
  
  .feed-item-container.legacy-style .feed-item-main, #channel-feed-post-form {
    border-bottom: 1px solid #585858;
  }
  
  .channel-header .secondary-header-contents {
    background: rgba(0, 0, 0, 0.5);
  }
  
  
}
