style.css:
@-moz-document url-prefix(https://www.tradingview.com/chart) {
  

.widgetbar-tabs {
  position: absolute;
  height: 100%;
  width: 45px;
  right: 0;
  top: 0;
  background: #2F3038;
}


.tv-side-toolbar {
  background: #2F3038;
  width: 51px;
  color: #babdc2;
  border-right: 1px solid #c9cbcd;
}


.header-chart-panel {
  display: block;
  position: relative;
  z-index: 1;
  padding: 5px 5px 0;
  background-color: #2F3038;
}

#footer-chart-panel {
  padding: 0 5px;
  background-color: #2F3038;
  position: relative;
  text-align: center;
}



.widgetbar-tab {
  cursor: pointer;
  position: relative;
  border-bottom: solid #c9cbcd 1px;
  background: #2F3038;
  padding: 9px 0;
}

.widgetbar-tab:hover, .widgetbar-tab.active {
  background: #2F3038;
}


#chart-area {
  background: #2F3038 !important ;
}

.widgetbar-tab-spacer {
  height: 28px;
  background: #2F3038;
  border-bottom: solid #c9cbcd 1px;
  text-align: center;
  text-transform: uppercase;
  font-size: 10px;
  font-weight: bold;
  line-height: 29px;
  color: #FFF;
  cursor: default;
}

.widgetbar-widgetheader {
  background: #2F3038;
  height: 34px;
  cursor: default;
  margin: 3px;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}


.symbol-list-item.success {
  color: #FFF;
}

.symbol-list-item:hover {
  background: #3B5D76;
}

.symbol-list-item:nth-child(even) {
  background-color: #2F3038;
}


.symbol-list-item:nth-child(odd) {
  background-color: #3C3C3C;
}

.widgetbar-widget {
  margin: 0 0 1px 0;
  position: relative;
  background-color:  #2F3038;
}


.dl-header-symbol-desc {
  color: #E0E0E0;
  font-weight: bold;
  font-size: 15px;
  margin: 0 7px 0 0;
}

.dl-header-price {
  font-size: 28px;
  font-weight: bold;
  color: #E0E0E0;
  min-height: 34px;
}

.dl-range-caption {
  position: absolute;
  bottom: 2px;
  width: 100%;
  text-align: center;
  color:#E0E0E0;
  font-size: 11px;
}

.dl-range-price-l, .dl-range-price-r {
  position: absolute;
  font-weight: bold;
  color: #E0E0E0;
  top: 25px;
}

.wl-symbol-edit {
  float: left;
  background: rgb(44, 40, 40);
  color: #2F3038;
  font-size: 12px;
  width: 100px;
  padding: 0 5px;
  border: solid #c9cbcd 1px;
  color: black;
  height: 30px;
  font-family: 'Open Sans',sans-serif;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}

.widgetbar-headerspace .button {
  padding-top: 5px;
  background-color: #2F3038;
  border: 1px #c9cbcd solid;
  font-size: 12px;
  line-height: 15px;
  width: 39px;
  height: 30px;
  float: left;
  display: block;
  position: relative;
  text-align: center;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  margin-left: 4px;
}
a {
  color: #0099d4;
  text-decoration: none;
}


.favored-list-container span {
  display: inline-block;
  height: 14px;
  position: relative;
  vertical-align: top;
  color: #FFFFFF;
  font-size: 11px;
  font-weight: bold;
  background: #2F3038;
  border: 1px solid #c9cbcd;
  border-width: 1px 1px 1px 0;
  padding: 7px 12px 7px;
  cursor: default;
}


.header-chart-panel .button, .hotlist-controls .button, .symbol-edit-widget .button, .bottom-widgetbar-content.backtesting .button {
  cursor: default;
  padding: 7px 12px 7px;
  border: 1px solid #c9cbcd;
  height: 14px;
  background: #2F3038;;
  color: #FFFFFF;
  font-size: 11px;
  font-weight: 600;
  float: left;
  display: block;
  position: relative;
}

.favored-list-container span:active, .favored-list-container span.active, .favored-list-container span.selected {
  background: #2F3038!important;
  color: #08C1FC;
}

.header-chart-panel .button.disabled, .header-chart-panel .button.disabled:active, .header-chart-panel .button.disabled.active {
  background: #282323!important;
  border-color: #FFFFFF;
  box-shadow: none;
}

input.symbol-edit, .symbol-search-dialog input {
  background-color: rgb(213, 213, 215);
  border: 1px solid #FFFFFF;
  width: 291px;
  /* height: 28px; */
  padding: 0 0 0 7px;
  color: #010D17;
  font: 13px 'Open Sans',sans-serif;
  font-weight: 600;
  float: left;
}

.save-load-buttons .title {
  position: relative;
  max-width: 136px;
  padding-left: 5px;
  padding-right: 5px;
  border-left: 0;
  background: #2F3038;
  cursor: pointer;
}

.layout-toggle-container .tv-button {
  padding: 0;
  width: 45px;
  height: 28px;
  position: relative;
  background: #2F3038;
}

#footer-chart-panel .bottom-toolbar-tab {
  position: relative;
  z-index: 1;
  float: left;
  height: 29px;
  background: #C5C5C5;
  border: solid #FFFFFF;
  border-width: 1px 1px 0 1px;
  margin: 0 -1px 0 0;
  font-size: 11px;
  cursor: pointer;
}

.save-load-buttons .separator {
  display: none;
  width: 7px;
  height: 28px;
  position: absolute;
  top: 0;
  right: 0;
  background: #2F3038;
}

.tv-side-toolbar .tools-group .button:not(.subgroup):active, .tv-side-toolbar .tools-group .button:not(.subgroup).active, .tv-side-toolbar .tools-group .button:not(.subgroup).selected, .properties-toolbar .tools-group .button:active, .properties-toolbar .tools-group .button.active, .drawing-favorites-toolbar .tools-group .button:active, .drawing-favorites-toolbar .tools-group .button.active, .tv-side-toolbar .tools-group .button.selected .main, .tv-side-toolbar .tools-group .button:active .side, .tv-side-toolbar .tools-group .button.active .side {
  background-color: #2D4257;
}

.ch-item-text {
  font-size: 13px;
  margin: 4px 0 -1px -4px;
  color: #A8AAB2;
  overflow-x: hidden;
  padding: 0 4px 1px;
  -webkit-border-radius: 2px;
  -webkit-background-clip: padding-box;
  -moz-border-radius: 2px;
  -moz-background-clip: padding;
  border-radius: 2px;
  background-clip: padding-box;
}

.ch-input .wrap textarea {
  background-color: rgb(27, 27, 27);
  border: 1px solid #AEAEAE;
  width: 175px;
  height: 17px;
  margin: 0;
  padding: 7px 26px 7px 8px;
  color: #FFFFFF;
  float: left;
  font: 13px 'Open Sans',sans-serif;
}

.ch-input .wrap { padding: 0px 0px 0; height: 34px; position: relative; overflow: hidden; 
}

a {
  color: #FF0088;
  text-decoration: none;
}

.ch-item-header {
  margin: 0;
  height: 16px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  line-height: 16px;
  color: rgba(9, 240, 161, 0.6);
}

.widgetbar-widgethandle {
  display: none;
  position: absolute;
  height: 5px;
  top: -3px;
  left: 0;
  width: 100%;
  background: #888989;
}

.tv-close-panel {
  width: 5px;
  height: 25px;
  border: solid #000000;
  border-width: 1px 0;
  background: #43809A no-repeat 50% 50%;
}

.note-block .name {
  display: inline-block;
  font-size: 13px;
  font-weight: bold;
  color: #02FF78;
  margin-top: 6px;
}

.note-block {
  position: relative;
  cursor: pointer;
  border-top: 1px solid #888888;
  padding: 9px 11px 12px 11px;
}

.symbol-list-item .plus {
  color: #00FF9E;
}

.symbol-list-item .minus {
  color: #FF2D00;
}

.dl-header-price .plus, .dl-header-price-extended .plus, .dl-header-change.plus, .dl-header-change-extended.plus {
  color: #00FF9E;
}

.dl-header-price .minus, .dl-header-price-extended .minus, .dl-header-change.minus, .dl-header-change-extended.minus {
  color: #FF2D00;
}

.dl-range-bar {
  height: 7px;
  background: #00FF9E;
  position: absolute;
  -moz-transition: all .3s;
  -webkit-transition: all .3s;
  -o-transition: all .3s;
  -ms-transition: all .3s;
  transition: all .3s;
  border: solid #fff;
  border-width: 0 1px;
  margin-left: -1px;
}

.dl-range-bar.low {
  background: #FF2D00;
}

.ch-highlight-user {
  background: #000000;
}

.dl-bidoffer-spread span.dl-bid {
  color: #322B2B;
  background: #00FD50;
  margin-right: 5px;
}

.dl-bidoffer-spread span.dl-ask {
  color: #343131;
  background: #FF004D;
}
}
