@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kibergrad.com"){
  
   body, html {background: rgba(0,0,0,.05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;	background-attachment: fixed !important;	background-clip: border-box !important;	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important; font-family:Constantia!important}
    
  /* transparent & remover */
  div#site, div#ad .r1, div#entment .r2, div#ad div.r, #social_buttons, .lineborder, .hover-static .item-menu-genre-r, .hover .item-menu-genre-r, .hover-static .item-menu-genre-l, .hover .item-menu-genre-l, .route, .song, .hide-txt, .pages-x > li, #content .activ-x2 a, .pages-x li:hover a, .pages-x li a, .wrap .activ-x span, .tabs-x li:hover span, .tabs-x li, .tabs-x span, .menu-button-l, .menu-button-r, .menu-button-name, .menu-item-pointer, .item-menu-abc-r, .item-menu-abc-l, .item-menu-abc-name, .item-menu-year-l , .item-menu-year-r, .item-menu-year-name, .footer-minimize-icon, #brcr #info, .short-album-cover, #page_artist .mp-box, #page_artist .mp-box .about, #page_artist .mp-box .mp-holder, #page_artist .biography .shadow, .album-list > li, #heading #info, div#bottom, .btn, .album-list li .alignleft span, .song, .list-song, .minipl .jp-progress, .pg {background:transparent!important;border:none!important}
  
  #descriptionpage, .hint, #brcr, .em-select-top, .block-artist-short-top, .short-music a, .short-album-cover img, .count-o u, .album-list li .alignleft, .scroll li img, .scroll2 li img, .list-song > div, #newslist, .sort-value, #jp-self .jp-progress, #player-box .jp-progress, #player-box .jp-play-bar, #player-box .ui-slider-range, #player-box .jp-volume-bar  {border:none!important}
   
  /* box & shadow */
  div#site {background: rgba(0, 0, 0,  0.85)!important; box-shadow: 0px 0px 5px rgba(255, 0, 0,  .9 ) !important;border-radius:10px!important}
  
  #menuitemblock {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 45%, rgba(55, 55, 55, 55.75) 100%)!important;border-radius:10px!important;border:none!important}
  
  #brcr, .em-select-top, .mp .jp-progress .jp-seek-bar, .drop-x, #player-box .jp-progress, #player-box .jp-volume-bar  {background: rgba(0, 0, 0,  0.8)!important; box-shadow: 0px 0px 3px rgba(255, 0, 0,  .9 )inset !important}
  
  .scroll li img, .scroll2 li img, .mp .jp-progress .jp-play-bar, #player-box .jp-play-bar, #player-box .ui-slider-range, #player-box .ui-slider-handle {background: rgba(255, 0, 0,  0.4)!important; box-shadow: 0px 0px 3px rgba(255, 0, 0,  .9 )inset !important}
  
  #artist_search, .block-artist-short-bottom, #page_artist .mp-box, .album-list > li, #heading, div#postbox textarea, div#postbox #postnickname, #subgenre_block, .sort-list  {background: rgba(0, 0, 0, 0.25)!important; color: #ff0000!important;box-shadow: 0px 0px 3px rgba(255, 0, 0, .9 ) inset!important;border: none !important;border-radius:5px!important}
   
  .btn, .count-o, .FP, .Jp, .bp, .ap  {background: rgba(0, 0, 0, 0.85)!important; color: #ff0000!important;box-shadow: 0px 0px 2px rgba(255, 0, 0, .9 ) inset!important;border: none !important;border-radius:5px!important;text-shadow:none!important}
   
  #vk_groups {opacity:.0!important}
  #vk_groups:hover {opacity:.8!important}
 
    /* search */
  #searchblock .search-input {background-color: rgba(0, 0, 0, 0.25)!important; color: #ff0000!important;box-shadow: 0px 0px 3px rgba(255, 0, 0, .9 ) inset!important;border: none !important;border-radius:5px!important}
  
  /* button */
  .hover-static .item-menu-genre-name, .hover .item-menu-genre-name {background: linear-gradient(to right bottom , rgba(155, 155, 155, 0.55) 45%, rgba(255, 0, 0, 0.25) 100%)!important;border-radius: 5px !important}
  
  #content .activ-x2, .pages-x li:hover ,  .menu-button:hover, .hover-static .item-subgenre-r, .item-subgenre:hover .item-subgenre-r, .hover-static .item-subgenre-l, .item-subgenre:hover .item-subgenre-l, .hover-static .item-subgenre-name, .item-subgenre:hover .item-subgenre-name, .button-tabs h2, .button-tabs .tab-left, .button-tabs .tab-right  {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 45%, rgba(55, 55, 55, 55.75) 100%)!important;border-radius:5px!important}
   
  /* icon */
  #headsite #logo {background: url(http://savepic.org/7225764.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
   #headsite #logo:hover{transform: scale(1.1,1.1) !important}
  
  #searchblock .search-button {background: url(http://diyostech.com/images/contact/128656-simple-red-square-icon-business-magnifying-glass-ps.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important} #searchblock .search-button:hover{transform: scale(1.1,1.1) !important}
  
   .img-next, .img-next2 {background: url(http://www.iconsearch.ru/uploads/icons/32pxmania/32x32/misc_26.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important; margin-bottom: -8px!important}
   .img-prew, .img-prew2 {background: url(http://boomonline.com/arrow-left.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important; margin-bottom: -8px!important}
  
  .back, .back2 {background: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons-256/simple-red-glossy-icons-arrows/009635-simple-red-glossy-icon-arrows-double-arrowhead-left.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;transform: scale(1.5,1.5) !important}
  .next, .next2 {background: url(http://cdn.mysitemyway.com/etc-mysitemyway/icons/legacy-previews/icons-256/simple-red-glossy-icons-arrows/009636-simple-red-glossy-icon-arrows-double-arrowhead-right.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;transform: scale(1.5,1.5) !important}
  .back:hover, .back2:hover, .next:hover, .next2:hover {transform: scale(1.8,1.8) !important}
  
  #playbar .play, .jp-play .play, #player-box .jp-play  {background: url(http://icons.iconarchive.com/icons/icons-land/vista-multimedia/256/Play-1-Normal-Red-icon.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  #playbar .play:hover, .jp-play .play:hover, #player-box .jp-play:hover  {transform: scale(1.2,1.2) !important}
  
  .jp-pause .pause, #player-box .jp-pause {background: url(https://cdn2.iconfinder.com/data/icons/iconslandplayer/PNG/256x256/PlainGreen/PauseNormalRed.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  .jp-pause .pause:hover, #player-box .jp-pause:hover {transform: scale(1.2,1.2) !important}
  
  #playbar .download, .album-list .download {background: url(http://www.7zip-free.ru/Knopka-scachat.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important; margin-bottom: -3px!important}
  #playbar .download:hover, .album-list .download:hover {transform: scale(1.2,1.2) !important} 
  
  .menu-button-main {background: url(https://mocapay.com/wowbao/images/home-red.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important; margin-bottom: -3px!important}
  
  .em-select-eject, #page_artist .moreimage {background: url(http://www.msqq.com/d/file/icon/2014-04-01/9af1ec9b1583b6f079474ed6d21b6507.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;transform: scale(1.3,1.3) !important}
  
   #artist_search {background: url(http://c.dryicons.com/images/icon_sets/iconika_red_icons/png/48x48/search.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  
  .artist-search-clear-image {background: url(https://homepages-fb.thm.de/jbusse/images/icons/button_cancel.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  
  #add_comment {background: url(https://www.hscripts.com/freeimages/icons/web-basic-icons/addcomment/128/addcomment-clipart5.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  #postsend {background: url(http://corp-nedv.ru/images/send_button.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  
  .rou-x img.not-act {background: url(https://www.hscripts.com/freeimages/icons/web-basic-icons/refresh/refresh5.gif) no-repeat scroll 0% 0% transparent !important;background-size: contain !important}
  
  #linkabout-box #linkbutton {background: url(http://4.bp.blogspot.com/-nf1MCyogS68/VJau9TxrK7I/AAAAAAAAAHk/ra5qdLIDVeo/s1600/Download-button-red1.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;transform: scale(1.4,1.4) !important}
   
  /* text color */
  .item-menu-genre-name, .song p, .song a, .pages-x li a, .wrap .activ-x span, .tabs-x li:hover span, .tabs-x span, .hint, .year-all-text, .footer-minimize-text, #brcr #title a, #brcr #title span, #brcr #title h1, .em-select-value, .block-artist-short h3, .short-music a, a, #page_artist .mp-box .about, #brcr #info, div#postbox .hotkey, div#bottom #service, div#bottom .feedback span a, div#bottom .feedback span, #additionalarea h2, #newslist > h1, .item-subgenre-name, .hover-static .button-tab-item h2, #descriptionpage h1, .minipl .jp-current-time, .minipl .jp-duration, div#comments .t, #player-box .jp-current-time, #player-box .jp-duration-x, #linkabout-box h1, #linkabout-box h2   {color:#bc1111!important;text-decoration:none!important;text-shadow:none!important}
  
   .menu-button-name, .item-menu-abc-name, .item-menu-year-name, .em-select-value:hover, .short-music a:hover, .block-artist-short h3:hover, a:hover, #page_artist .about .title, #heading #title, div#comments, #page_artist .sorttbl, #additionalarea, .list-song > div > div > div, #newslist > div, .sort-out, .sort-value, #descriptionpage p, div#comments .time, #linkabout-box .line-x, #linkabout-box .line-x span {color:#ff3452!important;text-decoration:none!important}
  
  #linkabout-box #linkbutton .megabytes {color:#0f0!important;text-decoration:none!important;text-shadow:none!important}
  
  .song a:hover, .item-menu-abc :hover, .item-menu-year-name:hover  {color:#000!important;text-decoration:none!important;text-shadow: 0px 0px 3px #f00!important}
  
 /*ads*/
 #MarketGidScriptRootC580390, #MarketGidScriptRootC580391 {display:none!important}}

