  }
  
  /*#configTreeBody::-moz-tree-cell-text(user) {
    color: tomato !important;
  }*/

    scrollbar {
    -moz-appearance: none !important;
    background: #181818 !important;
    border-left: 1px solid #161616 !important;}

  scrollbar thumb {
      -moz-appearance: none !important;
      background: #636363 !important;
      background-clip: padding-box !important;
      border: none !important;}

  scrollbar thumb:not(:active):hover, scrollbar thumb:hover:active {
      background: #0f80fd !important;
      background-clip: padding-box !important;
      -webkit-transition: background 1s !important;
      transition: background 1s !important;}

  scrollbar thumb:active,
  scrollbar thumb[active="true"] {
      border: none !important;
      background: #363636 !important;
      -webkit-transition: background 1s !important;
      transition: background 1s !important;
      background-clip: padding-box !important;}

  scrollbar scrollbarbutton, scrollbar gripper, scrollcorner {
      -moz-appearance: none ! important;
      border: none !important;
      -moz-appearance: none !important;
      background: #181818 !important;
      -webkit-transition: background 2s !important;
      transition: background 2s !important;
      transition-timing-function: ease !important;
      -webkit-transition-timing-function: ease !important;}

  scrollbar[orient="vertical"] > scrollbarbutton {
      min-height: 17px !important;
      max-height: 17px !important;
      min-width: 7px !important;
      max-width: 7px !important;}

  scrollbar[orient="horizontal"] > scrollbarbutton {
      min-height: 15px !important;
      max-height: 15px !important;
      min-width: 7px !important;
      max-width: 7px !important;}

  scrollbar > scrollbarbutton[disabled="true"] {opacity: 0.8 !important;}

  scrollbar > scrollbarbutton:not([disabled="true"]):hover {
      border: none !important;
      background: #0f80fd !important;
      -webkit-transition: background 1s !important;
      transition: background 1s !important;
      transition-timing-function: ease !important;
      -webkit-transition-timing-function: ease !important;
      background-clip: padding-box !important;}

  scrollbar > scrollbarbutton:not([disabled="true"]):hover:active,scrollbar > scrollbarbutton:not([disabled="true"])[active="true"] {
      -moz-appearance: none !important;
      border: none !important;
      background: #0f80fd !important;
      -webkit-transition: background 1s !important;
      transition: background 1s !important;
      transition-timing-function: ease !important;
      -webkit-transition-timing-function: ease !important;
      background-clip: padding-box !important;}
  
  menupopup {
    opacity: 1 !important;
    border: none !important; 
    padding: .4em 0 !important;
    background: none !important;
    box-shadow: none !important;
    -moz-appearance: none !important;
    -moz-window-shadow: none !important;
    border-radius: 0px !important;
    color: #BDC3C7 !important;      
    background: #1c2126 !important;
  }
    
  menupopup .menu-text, menupopup .menu-iconic-text {
      font-size: 120% !important; 
      font-family: candara !important; 
      color: #BDC3C7 !important; 
  }

  .menuitem-iconic[disabled="true"] .menu-text, .menuitem-iconic[disabled="true"] .menu-iconic-text {
      color: rgb(130,130,130) !important; 
  }

  menuseparator {
      opacity: 0 !important; 
  }

  menu:hover, menuitem:hover, menuitem.menuitem-iconic:hover, menuitem.menuitem-non-iconic:hover {
      -moz-appearance: none !important;
      background: rgba(0,0,0,.4) !important;
      box-shadow: none !important;
  }

  menu, menuitem, menuitem.menuitem-iconic, enuitem.menuitem-non-iconic {
      -moz-appearance: none !important;
      background: none !important; 
  }     
  
}

