style.css:
@-moz-document domain("pinboard.in") {

/*
Modern Pinboard Style

Designed to be used for URLs on the domain of "pinboard.in".

Author: Brendan Murty
Website: https://github.com/brendanmurty/pinboard-user-style
*/

/* Hide elements */

#timer,
#logo span,
.tab_spacer {
  display: none;
}

/* Layout */

body {
  margin: 20px;
  font-family: "Helvetica Neue", "Microsoft YaHei", Arial, Helvetica, sans-serif;
  background-color: #000;
  color: #fff;
}

/* Privacy lock */

#logo a[href="/settings/privacy"] {
  background-color: #fff;
  padding-right: 3px;
  margin-left: 5px;
}

#banner {
  background-color: #1e1ed0;
  margin: 0 auto 10px;
  border: 0 none;
  padding: 25px 20px;
  width: 1010px;
}

#banner a {
  color: #fff;
}

#main_column {
  margin: 0 auto 10px;
  padding: 0;
  width: 700px;
  float: left;
}

#right_bar,
#tag_cloud {
  margin-top: 10px;
  float: right;
  width: 300px;
  margin-left: 10px;
}

a.tag,
input#tags,
input#tags_input,
form[action="/add"] a,
form[action="/add"] .pin-ac,
form[action="/edit/"] .pin-ac {
  text-transform: lowercase;
}

#tag_cloud a.tag_heading_selected {
  color: #fff;
}

#searchbox input[type=text] {
	width: 275px;
  margin-bottom: 5px;
}

#searchbox .search_button {
  display: inline-block;
  float: none;
  color: #fff;
  margin-bottom: 5px;
}

#searchbox label[for='fulltext'] {
  font-size: 90%;
}

#top_menu a[href='/logout/'] {
  color: #9b9be7;
}

a,
a:visited {
  color: #fff;
}

a.bookmark_title,
a.bookmark_title:visited,
#main_column a.unread,
#main_column > p a:visited {
	color: #fff;
}

a.tag,
a.tag:visited {
  color: #a51;
}

a.twitter_user,
a.twitter_user:visited {
  color: #888;
}

a.url_display,
a.url_display:visited {
	color: #888;
}

#bulk_links {
  margin-bottom: 0;
}

#nextprev,
#bottom_next_prev,
#main_column > p {
  padding: 0 10px;
  margin-top: 5px;
}

#footer {
	border-top: 1px solid #ccc;
	padding: 20px 0 10px;
	margin: 20px auto 10px;
	width: 1030px;
}

#footer hr {
  border: 0 !important;
}

/* Link list */

.bookmark {
	width: 715px;
	padding: 10px;
  background-color: #222;
}

.star {
  float: right;
}

.display {
  max-width: 625px;
}

.private {
	border: 0;
}

a.unread {
  font-weight: bold;
 	color: #51a;
}

.star {
  margin-left: 0;
  margin-right: 5px;
}

.edit_checkbox input {
  margin-top: 15px;
}

#bulk_edit_options {
  width: 730px;
}

#bulk_edit_box {
  border: 0;
}

/* Settings */

.tab,
.tab_spacer,
#tab_panes,
#import_history_table {
  border: 0 !important;
}

#tab_panes {
  padding: 10px;
}

/* Forms */

#main_column form {
  background-color: #000;
  color: #fff;
}

input[type="button"],
input[type="submit"],
input[type="reset"] {
  padding: 4px 8px;
}

.search_button input[type="checkbox"] {
  float: left;
}

td,
h2.settings_heading,
.tabs,
h2.tour_heading,
#edit_bookmark_form,
#edit_bookmark_form form {
  background-color: transparent;
}

.tab_selected {
  background-color: #444;
}

.help {
  padding: 2px 3px;
}

}
