/*Удаляем рекламу*/
body > div.rslider-container, .rslides,.transparent-btns,.transparent-btns1, .transparent-btns1.transparent-btns.rslides
{
    display: none!important;
  }
/*Удаляем счетчики посещений*/
.swiper-wrapper
{Display:none!important;}
/*Поправляем отступ*/
body > footer > div > section:nth-child(4) > div > div:nth-child(4)
  #blog
 {
    padding-top: 4%; /* Поле сверху */
    padding-bottom: 4%; /* Поле снизу */
  }
/*Смена цвета плавающей шапки*/
  .navbar
  {
    background-color: #0e0e0e;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#151515), to(#040404));
    background-image: -webkit-linear-gradient(top, #151515, #040404);
    background-image: -moz-linear-gradient(top, #151515, #040404);
    background-image: -o-linear-gradient(top, #151515, #040404);
    background-image: linear-gradient(to bottom, #151515, #040404);
  }
}
