style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.radiotunes.com") {

    #body {
       background-color: #12151A;
       border-color: #E1E4E6;
    }
   #page > header {
       background-color: #12151A;
       border-top: 5px solid #424242;
   }
   #channel-selector .backdrop {
       background: repeat scroll 0 0 #12151A;
   }
   #toolbar button {
       color: #9C9C9C;
       background-color: #12151A;
   }
   #toolbar button:hover {
       color: #BABABA;
       background-color: #12151A;
   }
    #hero {
        background-color: #12151A;
        border-color: #424242;
    }
   .popup, .popup-share, .popdown-reveal {
       background-color: #12151A;
   } 
   .content-box .box-title {
       background-color: #12151A;
   }   
   ul.similar-channels li {
       background-color: #424242;
   }
   #track-history .item {
       background-color: #424242;
       color: #B0B0B0;
   }
   #track-history .item:hover .info, #track-history .item.active .info {
       color: #B0B0B0;
   }
    #toolbar-container {
       background-color: #12151A;
       border-color: #424242;
    }
    #page > footer {
       background-color: #12151A;
   }
   #foot-content {
       background: #12151A;
       padding: 20px 0;
       overflow: hidden;
       z-index: 0;
   }
}
