style.css:
@-moz-document domain("ithome.com") {
  /* 大标题文字广告*/
  [id^="tt"],[id^="_"] {
    display: none !important;
  }

  a[href*="http://go.ruanmei.com/url.aspx"] {
    display: none !important;
  }

  #topest-banner {
    display: none !important;
  }

  /* #topest-banner 带来的样式错位 */
  #hd {
    top: 0 !important;
  }

  body {
    padding-top: 35px !important;
  }

  /*****侧边按钮*****/
  /* 只留下回到顶部和去评论按钮 */
  #side_func a:not(#gotop):not(#gocomm) {
    display: none !important;
  }
  /* 头部导航 */
  #con {
    padding-top: 20px;
  }

  /* 底部分类板块 */
  .con_2 {
    display: none !important;
  }

  /* 底部去除链接 */
  .lk {
    display: none !important;
  }

  /* 内容栏 */
  /* 购物广告，标记成已删除 */
  a[href^="http://lapin.ithome.com"] {
    opacity: 0.6;
    text-decoration: line-through;
  }
  /*去除adblock 提示*/
  .AdblockBanner {
     display: none !important;
  }
  #hd {
     position: fixed !important;
  }
}

@-moz-document domain("ithome.com") {
  /* 头部焦点区域 */
  .focus_area,
  .lf {
    display: none !important;
  }

  /*****脚本生成的广告*****/
  .focus_area + div,
  .con-recom,
  .bx-recom,
  .con-recom + a,
  #crf1,
  #hd_float {
    display: none !important;
  }

  /*  顶部导航条 */
  #nav {
    width: 900px !important;
    height: 36px !important;
  }

  .tb-item a.item-link {
    height: 36px !important;
  }

  /* 菜单导航栏（菜单+搜索+LOGO）*/
  [id^="top5"] {
    /*display:none;*/
    width: auto !important;
    background: none !important;
    padding: 0 !important;
    margin: 0 !important;
    margin-left: -150px !important;
    position: fixed !important;
    top: 0;
    left: 50%;
    z-index: 1600;
  }

  [id^="top5"] > :not(.sc) {
    display: none !important;
  }

  [id^="top5"] .sc {
    margin: 0 !important;
  }

  /********************内容栏********************/
  #con {
    width: 900px;
    padding: 0;
  }
  #con .rt {
    margin: 0 auto;
    width: 820px;
  }
  #con .con-block,
  #con .rt,
  #con .rt .nlst {
    float: none;
  }
  .con-block {
    padding: 0 !important;
  }
  /*分类标题：最新|最热*/
  .rt .nlst {
    padding-top: 20px;
  }
  .rt .nlst .ct {
    width: auto;
  }
  .rt .nlst .lst {
    width: auto;
    height: auto;
    padding-top: 10px;
    overflow: visible;
  }

  /*最新更新*/
  .rt .nlst .new-list ul {
    width: 50%;
    margin: 0;
  }
  .rt .nlst .new-list ul li {
    width: 97%;
    box-sizing: border-box;
  }
  .rt .nlst .new-list ul li.top {
    width: 97%;
  }
  li.mnow {
    width: auto !important;
    min-width: 395px !important;
  }
  li span.title {
    width: auto;
  }

  /*底部分页*/
  .index .page_nav {
    width: auto;
    padding-bottom: 10px;
  }

  /*最热排行*/
  .rt .nlst .hot-list .bx {
    width: 45%;
  }

  /* 侧边栏 */
  .sidebar {
    display: none !important;
  }

  /********************侧边按钮********************/
  #side_func {
    height: 116px !important;
    margin-right: -508px !important;
    /* 900/2 + 58 内容栏一半+自己的宽度 */
  }

  /* 文章页面 */
  #wrapper {
    margin: 0;
    padding: 0;
  }
  .content {
    width: auto !important;
    padding: 20px !important;
    float: none !important;
  }
  /* 图片相关文章 */
  .rela-post,
  .related_buy {
    display: none !important;
  }
  /* 文字相关文章 */
  .related_post ul {
    width: 100%;
  }
  .related_post ul.list_3 li {
    width: calc(50% - 20px); /* calc(50% - 20px) */
  }
  /* 分享组件 */
  .shareto {
    display: none;
  }
}

@-moz-document domain("dyn.ithome.com") {
  /* 评论框*/
  .post_comment,
  .add_comm {
    box-sizing: border-box;
    width: 100% !important;
  }
  .add_comm textarea {
    width: calc(100% - 30px) !important; /* 15+15*/
  }
  .add_comm .comm-con {
    width: calc(100% - 120px) !important; /* 105+15*/
  }
  /* 评论列表 */
  .comm_list ul.list li.entry {
    width: auto !important;
  }
}

@-moz-document domain("lapin.ithome.com") {
  /* 购物广告，标记成已删除 */
  a[href^="http://lapin.ithome.com"] {
    opacity: 1;
    text-decoration: none;
  }
}

@-moz-document domain("quan.ithome.com") {
  #con {
    width: auto;
  }
  /*覆盖首页*/
  #side_func {
    width: 50px !important;
    height: 50px !important;
    bottom: 55px !important;
    margin-right: -616px !important;
  }
  /* 页码居中 */
  .pagenew {
    text-align: center !important;
    width: 100% !important;
  }
  .page_nav .page_num a,
  .page_nav .page_num span.current {
    float: none !important;
    display: inline-block !important;
  }

  /* 底部去除链接 */
  #foot {
    padding-top: 30px;
  }
}
