style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("smart-lab.ru") {
/*v.0.2, 18-05-2015*/
#container,
div.center,
#wrapper
{
    min-width: 1200px !important;
}
.console_box
{
    width: 1184px !important;
}    
#content_box,
#inindex,
#wrapper div.content_desc
{
    width: 680px !important;
}
#content
{
    width: 660px !important;
}
#content[style*="width:100%"]
{
    width: 100% !important;
} 
.comment .content .text
{
    padding: 5px 25px 10px 8px !important;
    font-size: 11px !important;
} 
.comment .info
{
    margin: 0 50px 20px 13px !important;
}
/*комменты в разделе Чат (слева нет марджина)*/
div.comments.padding-none .comment .info
{
    margin: 0 50px 20px 0 !important;
}
.comment .voting
{
    top: 2px !important;
}
.comments.padding-none .voting
{
    top: 18px !important;
}
}
