@-moz-document regexp("https?://(?:www.)?(webtoons|comic.naver).(.*)/(detail|viewer).*") {
/* ↓----------------------[ both site ]----------------------↓ */

body {background-color: /*[[bgcolor]]*/;}

/* ↑----------------------[ both site ]----------------------↑ */
/* ↓----------------------[ naver ]----------------------↓ */

div.wt_viewer {background: none!IMPORTANT;}
#footer * {color: #adadad;}
#footer .info span {color: #ddd;}
#footer address {color: white;}
#footer a {color: #20ec2b;}

.tit_area {
    border-top: 2px solid #2a2a2a;
    border-bottom: 1px solid #2a2a2a;}

.tit_area .view {
    border-bottom: 0;
    background: #333;}

.tit_area .view h3 {color: lightgrey}

#header h1 [class="h_naver"] {
    background: url("data:image/png;base64,") 0 5px no-repeat;}
#header h1 [class="h_comic"] {
    background: url("data:image/png;base64,") no-repeat;}

#header h1 .bar {background-color: #555;}

#header h1 [class="h_novel"] {
    background: url("data:image/png;base64,") no-repeat;}

/* ↑----------------------[ naver ]----------------------↑ */
/* ↓----------------------[ line ]----------------------↓ */

a {color: #EEE}
.viewer_lst .day_info {color: #d1d1d1;}
.viewer_lst .dsc_encourage {color: #5f5f5f;}

::-webkit-scrollbar {
  width: 9px;   
  height: 9px;
  background: #303639}
::-webkit-scrollbar-thumb:vertical, ::-webkit-scrollbar-thumb:horizontal {
  min-height: 5px;
  min-width: 5px;
  outline-offset: -2px; 
  outline: 1px solid #6a6a6a; 
  background-color: #6a6a6a}
::-webkit-scrollbar-thumb:hover {
  min-height: 5px;
  min-width: 5px;
  outline: 1px solid #4a4a4a; 
  background-color: #4a4a4a}
::-webkit-scrollbar-thumb:active {   
  min-height: 5px;
  min-width: 5px;
  outline: 1px solid #20ec2b; 
  background-color: #20ec2b}
::-webkit-scrollbar-button:start:decrement, 
::-webkit-scrollbar-button:end:increment {
    display: none}

.sel_bx .scrollbar-v .scrollbar-thumb {
  min-height: 5px;
  min-width: 5px;
  outline-offset: -2px; 
  outline: 1px solid #6a6a6a; 
  background-color: #6a6a6a}
.sel_bx .scrollbar-v .scrollbar-thumb:hover {
  min-height: 5px;
  min-width: 5px;
  outline: 1px solid #4a4a4a; 
  background-color: #4a4a4a}
.sel_bx .scrollbar-v .scrollbar-thumb:active {   
  min-height: 5px;
  min-width: 5px;
  outline: 1px solid #20ec2b; 
  background-color: #20ec2b}

.btn_logo {background: url("http://i.imgur.com/1uY1WVK.png") no-repeat}
.btn_top {background: url("http://i.imgur.com/jPCPrDy.png") no-repeat}
.btn_top:hover {background: url("http://i.imgur.com/0WG6Csq.png") no-repeat}

.viewer .challenge_spot_list .skin {background: url("http://i.imgur.com/jxpFdnF.png") no-repeat 0 -950px!IMPORTANT;}
.btn_episode_fd {background: url("http://i.imgur.com/Z2OIh5U.png") no-repeat;}
.u_cbox .u_cbox_recomm_set .u_cbox_ico_recomm, .u_cbox .u_cbox_recomm_set .u_cbox_ico_unrecomm, .u_cbox .u_cbox_comment .u_cbox_ico_best, .u_cbox .u_cbox_ico_reply, .u_cbox .u_cbox_paginate .u_cbox_more_wrap .u_cbox_ico_more, .u_cbox .u_cbox_paginate .u_cbox_ico_page, .u_cbox .u_cbox_ico_editor, .u_cbox .u_cbox_tool .u_cbox_btn_reply, .u_cbox .u_cbox_tool .u_cbox_btn_reply_on, .u_cbox .u_cbox_paginate strong.u_cbox_page, .u_cbox .u_cbox_info_sub .u_cbox_work_box .u_cbox_btn_edit, .u_cbox .u_cbox_info_sub .u_cbox_work_box .u_cbox_btn_delete, .u_cbox .u_cbox_edit .u_cbox_upload .u_cbox_btn_upload {
    background-image: url("http://i.imgur.com/tb5iL8i.png");
    background-repeat: no-repeat;}

.paginate.v2 em {background: url("http://i.imgur.com/fMmgYNT.png") no-repeat}
.paginate.v2 em:hover {background: url("http://i.imgur.com/w5lIT98.png") no-repeat}

div.ly_loginbox.bg_wh {
    background: #1E1E1E;
    border-top: 2px solid rgba(74, 74, 74, 0.58);
    -moz-box-shadow: inset 0 0 5px #000;
    -webkit-box-shadow: inset 0 0 5px #000;
    box-shadow: inset 0 -1px 5px #000;}

span.u_cbox_nick, p.t_login, div.creator_note h2, h5.u_cbox_title, div.title_area h2 span {color: #dedede!IMPORTANT;}
.login_area .bg_line, .ly_loginbox.bg_wh .bg_line {background: #2a2a2a;}
.u_cbox .u_cbox_paginate .u_cbox_page:hover {
    color: white;
    -webkit-transition: all .4s;
    -moz-transition: all .4s;
    -ms-transition: all .4s;
    -o-transition: all .4s;
    transition: all .4s;
    text-shadow: 0 0 .4em rgba(255,255,255,0.3);}

.tool_area, #challengeRoundUp, .challenge_spot {background: #303639!IMPORTANT}
.episode_area {background: rgba(48, 54, 57, 0.95)!IMPORTANT}

.sel_bx {
    border: 0;
    background: #1C1C1C;}
.tool_area .cite {background: #616C73;}
.tool_area [class^=ver_] {border-top: 0;}
.episode_area .title_area .sort_area .bar {background: #EEE;}

.u_cbox {background: none;}

.u_cbox_text {color: #e5e5e5}
.u_cbox .u_cbox_comment_box {
  padding: 10px 0;
  border-bottom: 2px dotted #333;}
.comment_area .creator_note, .lst_type1 {border-bottom: 2px dotted #333;}
.lst_type2, .lst_type1 li {border-top: 2px dotted #333;}
.tool_area .subj_info .subj:hover {color: #20ec2b}

.cont_box {
    /*background: #1E1E1E;*/
    width: auto}
.u_cbox_text {color: #e5e5e5}

.copyright {border-top: 2px dotted #2a2a2a;}
.viewer .challenge_spot_inner h3 {color: #eee;}

#footer {
    background: #303639;
    border-top: 2px dotted #2a2a2a;}
.notice_area {
    background: #303639;
    border-bottom: 2px dotted #2a2a2a;}
.viewer .challenge_spot {
    background: none;
    border-top: solid 1px #4a4a4a;}

.u_cbox .u_cbox_write .u_cbox_inbox .u_cbox_text, .u_cbox .u_cbox_write .u_cbox_inbox {background: #616c73;}
.u_cbox .u_cbox_head {
  border-bottom: 0;
  line-height: 10px;}
.u_cbox .u_cbox_write .u_cbox_inbox .u_cbox_guide, .u_cbox_title {color: #eee}
.u_cbox .u_cbox_name {color: #5fed36;}
.u_cbox .u_cbox_recomm_set .u_cbox_btn_recomm, .u_cbox .u_cbox_recomm_set .u_cbox_btn_recomm, .u_cbox .u_cbox_recomm_set .u_cbox_btn_unrecomm {background-color: transparent}
.u_cbox .u_cbox_btn_report:hover {
    -webkit-transition: all .4s;
    -moz-transition: all .4s;
    -ms-transition: all .4s;
    -o-transition: all .4s;
    transition: all .4s;
    color: #ec2637;}
.u_cbox .u_cbox_paginate .u_cbox_page {color: #474747;}
p.day_info {color: #757575;}
p.dsc_encourage {color: #bababa;}
div.creator_note h2, h5.u_cbox_title, .u_cbox .u_cbox_paginate .u_cbox_page:hover, div.title_area h2 span {color: #dedede}
p.author {color: #adadad;}
.foot_app .foot_down_msg .txt {color: #6A6A6A;}
.comment_area {padding: 85px 10px}
.aside.viewer {padding-right: 155px}

/* ↑----------------------[ line ]----------------------↑ */
}

@-moz-document regexp("https?://(?:www.)?(channel.line.naver|nid.naver).(.*)") {
/* ↓----------------------[ both ]----------------------↓ */

body {background-color: #1e1e1e;}
.btn_login.focus {border: 0}
input.int {background-color: #1c1c1c;}
.MdInputTxt01, .input_row, .input_row.focus {
    border: 1px solid transparent;
    border-top: 1px solid #171717;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.1);
    border-bottom: 1px solid #333;
    background-color: #1c1c1c;}
.MdBtn03Login, .btn_login {
    background-color: #212121;
    border: 1px solid rgba(0,0,0,0.05);
    border-bottom: 5px solid #37C65C;
    border-radius: 10px;}
.MdBtn03Login:disabled {
    background-color: rgba(156, 156, 156, 0.59);
    border-bottom-color: rgba(156, 156, 156, 0.59);}
.MdBtn03Login:disabled:hover {
    background-color: rgba(156, 156, 156, 0.59);
    border-bottom-color: rgba(156, 156, 156, 0.59);}
.MdBtn03Login:disabled, .btn_login, .MdBtn03Login {
    -webkit-transition: all .2s;
       -moz-transition: all .2s;
        -ms-transition: all .2s;
         -o-transition: all .2s;
            transition: all .2s;}
.MdBtn03Login:hover, .btn_login:hover {
    -webkit-transition: all .4s;
       -moz-transition: all .4s;
        -ms-transition: all .4s;
         -o-transition: all .4s;
            transition: all .4s;
    background-color: #37c65c;
    border-bottom-color: #37c65c;}
.MdInputTxt01 > input {color: #9CA0A5;}

.btn_login input {border: 0;}

.sp[id="label_login_chk"] {background: none}

.int_jogin {background: url("http://i.imgur.com/NN31wQ2.png") no-repeat;}
.ch_g .int_jogin, .ch_b .int_jogin {background: url("http://i.imgur.com/e7A22Hu.png") no-repeat;}
    
#header .lang .sel {background: url("data:image/gif;base64,R0lGODlhFAAIAKECADQ0NDU1Nf///////yH5BAEKAAIALAAAAAAUAAgAAAIblI+pEqEBloEgngqcpJrLw1HWN2EZmZjoo5EFADs=") 100% 50% no-repeat;}
#header .lang .sel {
    border: 1px solid #000;
    color: #969696;
    background-color: #151515;}

/* ↑----------------------[ both ]----------------------↑ */
}
