style.css:
@-moz-document domain("dolceflirt.it") {
#container{
  background-image : url(http://i.imgur.com/QjgWeHq.png) !important ;
  background-attachment: fixed !important ;
}
#container.connected #header {
    background-image : url(http://i.imgur.com/sQvCcRT.png) !important ;
}
.idcard-member {
    background : url(http://i.imgur.com/6Hu21Yf.png) no-repeat !important ;
}
.scene .player {
    background: url(http://i.imgur.com/sARuEoF.png) no-repeat scroll -6px top transparent;
    height: 130px;
    padding-left: 13px;
    width: 175px;
}
.place > .transition {
    background: url(http://pldh.net/media/pokemon/trozei/151.png) no-repeat scroll center top transparent;
    position: absolute;
    text-indent: -9000px;
}
.place > .transition:hover {
    background: url(http://i.imgur.com/Qwn8m7L.png) no-repeat scroll center top transparent;
    position: absolute;
}
.scene .response:hover {
    background: #72bbff none repeat scroll 0 0;
    color: white;
    cursor: crosshair;
}
#cadre_ext_body.disconnected {
    background: transparent url(http://i.imgur.com/D0gTMrY.png) no-repeat scroll center top;
}
#menu_landing {
    background: transparent url(http://i.imgur.com/DIwMDWX.png) no-repeat scroll center top;
    height: 60px;
    width: 100%;
}
#header, #header_landing {
    background: transparent url(http://i.imgur.com/7uwhgaq.png) no-repeat scroll center top;
    height: 135px;
    position: relative;
    width: 100%;
    z-index: 2;
}
}
