style.css:
@-moz-document
  url-prefix("http://www.pixiv.net/novel/"), 
  url-prefix("http://www.pixiv.net/series.php?") {
  /* ========== 小説一覧の２カラム化 ============================================== */
  .novel-items > li {
    box-sizing: border-box;
    -o-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    width: 50% !important;
    margin: 0 0 15px !important;}
  ._novel-item .novel-contents {
    box-sizing: border-box;
    -o-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    padding:0 !important;}
  ._novel-item {
    width: 95% !important;
    max-width: 100% !important;
    min-width: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;}
  ._novel-item .novel-contents .novel-right-contents {
    padding-left: 85px !important;}
  .novel-items{margin:0 0 0 20px !important;}
}

@-moz-document url("http://www.pixiv.net/novel/") {
  /* ========== 小説TOPで２列にならないように ====================================== */
  .novel-items > li {
    width: 100% !important;}
}
