style.css:
/*
	==== MyAnimeList.net - Layout Tweaks (Core Style) by Zealotus ====
	Pokes things into place...
*/
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
	td {
		line-height: 13px !important;
	}
	#menu_right input#topSearchText {
		padding-left:			6px !important;
		height:					17px !important;
	}
	/* Content */
	#content {
		padding:				4px 0 0 0 !important;
		margin:					0 !important;
	}
	#content>table {
		margin-top:				-4px !important;
	}
	#content td[style="padding-left: 8px;"] {
		padding-left:			2px !important;
	}
	#content div[id="horiznav_nav"] {
		margin-top:				0 !important;
		padding-top:			7px !important;
	}
	#content_nopad table tbody tr td div[class="borderDark"] {
		margin:					0 6px !important;
	}

	#rightcontentunder {
		display:				block !important;
		padding:				5px 0px !important;
	}
	div[id="rightcontent_largepad"] table tbody tr td {
		/*padding:				4px !important;*/
	}

	h1 div[style="float: right;"] {
		position:				relative !important;
			top:				1px !important;
			left:				-2px !important;
	}

	#content div.bgbdrContainer {
		margin-top:				8px !important;
		width:					819px !important;
	}

	/* Links and Texts */
	a:focus {
		outline:				none !important;
	}
	a:hover {
		text-decoration:		none !important;
	}

	/* Horizontal Navs */
	#horiznav_nav {
		padding:				6px 3px 3px 3px !important;
	}
	#horiznav_nav ul li a {
		font-weight:			bold !important;
		padding:				0.28em !important;
	}

	table[id=forumTopics] .forum_boardrow1 strong a:first-child:before {
		content:				'New ' !important;
		color:					#E10 !important;
	}
	table[id=forumTopics] .forum_boardrow2 strong a:first-child:before {
		content:				'New ' !important;
		color:					#E10 !important;
	}

	/* Profile */
	.profile_leftcell {
		padding-right:			0 !important;
	}
	.profile_leftcell div[style="padding: 0 5px;"] {
		padding:				0 !important;
	}
	.profile_leftcell div[class="normal_header"], .profile_leftcell div[style="margin: 2px 0pt;"],
		.profile_leftcell div[style="margin: 3px 0pt;"], .profile_leftcell div[style="margin-top: 6px;"],
		.profile_leftcell div[class="spaceit"] {
		padding-left:			3px !important;
	}
	#profileRows {
		padding:				0 !important;
	}
	#profileRows a {
		font-weight:			bold !important;
	}

	.borderClass {
		border:					0 !important;
	}

	div[style="padding: 14px 13px;"] {
		padding:				14px 0px !important;
	}
	div[style="margin: 0pt 10px 10px 0pt; float: left; display: none;"] {
		display:				inline !important;
	}

	.button_form {
		border:					none !important;
	}

	.button_form:hover {
		border:					none !important;
	}

	#dialog td td {
		border-width:			0 !important;
	}

	#copyright {
		/*display:				none !important;*/
		margin:					auto !important;
	}

	#panel_left {
		padding:				0 !important;
		width:					60% !important;
	}
	#panel_right {
		width:					39% !important;
		margin-left:			61% !important;
		padding:				0 !important;
	}

	
	.quotetext, .codetext {
		padding:				2px !important;
		margin-right:	10px !important;
	}

	#content div[style="padding: 0pt 15px;"] #dialog .textarea {
		height:					650px !important;
		width:					920px !important;
	}
	#content div[style="padding: 0pt 15px;"] {
		padding-left:			10px !important;
	}
	#content div[style="padding: 0pt 15px;"] #dialog {
		padding:				0px !important;
	}
	#content div[style="padding: 0pt 15px;"] #dialog td {
		padding:				10px !important;
	}
	#content #horiznav_nav {
		margin:					0 !important;
	}
	#content div[style="padding: 0pt 15px;"] #dialog div[style="margin: 10px 0px;"] .textarea {
		width:					654px !important;
		height:					120px !important;
	}
	#quickReply .textarea {
		margin-left:			141px !important;
		width:					809px !important;
		height:					300px !important;
	}
	#content div[style="width: 690px; overflow: hidden; margin: 0 auto;"] {
		width:					734px !important;
	}
	#content div[style="width: 530px; margin-left: auto; margin-right: auto;"] {
		margin:					-20px 0 0 0 !important;
		padding:				0 !important;
	}
	#content div[style="width: 530px; margin-left: auto; margin-right: auto;"] td {
		padding:				5px !important;
	}
	#content div[style="width: 530px; margin-left: auto; margin-right: auto;"] .textarea {
		width:					920px !important;
		height:					240px !important;
	}
	#content div[style="width: 450px; margin-left: auto; margin-right: auto;"] {
		width:					945px !important;
		margin-left:			12px !important;
	}
	#content div[style="padding: 0pt 15px;"] .textarea[rows="14"] {
		width:					840px !important;
		height:					360px !important;
	}
	#content div[style="padding: 0pt 15px;"] .textarea[rows="5"] {
		width:					840px !important;
		height:					200px !important;
	}
	#content #dialog {
		margin:					6px !important;
		border:					0 !important;
	}
	#content #dialog .textarea {
		width:					926px !important;
		height:					360px !important;
	}
	#content form[name="UserComment"] .textarea {
		width:					624px !important;
		height:					120px !important;
	}
	#content div[style="padding: 0pt 10px 10px;"] {
		padding:				0 !important;
	}
	#content div[style="padding: 0pt 10px 10px;"] #dialog td {
		padding:				4px !important;
	}
	#content div[style="width: 400px;"] .textareas {
		width:					954px !important;
		height:					420px !important;
	}
	#content #dialog {
		margin:					6px auto !important;
	}
	#content #dialog .inputtext[name="cpctext"] {
		width:					594px !important;
		height:					320px !important;
	}
	/* ==== START BETA AREA ==== */
	/* THIS IS STUFF I'M NOT SURE IF THEY'RE WORKING CORRECTLY... */
	/* ==== END BETA AREA ==== */
}
@-moz-document regexp("http://myanimelist.net/profile/.*/reviews") {
  div[style="padding: 14px 13px;"] {
    padding:        14px 8px !important;
  }
}
