style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
#wall_canvas,.feedback_link, #page_footer, #body_footer_wrap,.utility_link,.pagerArea,.commu_caption, .commu_info,#footer-ad,#live-footer,#site-footer,#global-footer,
  #tab_title_area,.btn_tree_text,#headerAd,#advertisement_box,#footer_ads, .share_right,#enquete-placeholder,.ichiba,#watch_niconicoukoku_box,.visitor_score,.login_wrapper,
  .user-ad-block,#wall_chip_area,#utility_link, #update-info, #zapping-block,#ichiba-block, #footer-utility-block, #user-ad-block, #program-utility-block, .sdk-block,
 [class*=notification-bar]{
    display: none !important;
  }
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
  }
  html, body{
    background: #1b1b1b !important;
  }
  .___comment-context-menu___YQzS_{
    background: #0f0f0f !important;
  }
  [data-input-mode="mouse"] .___player-status-layout___uarFS a:hover, .___player-status___3zkHS, .___ng-control___30H0y, #audience-mail-block .audience-mail-layout{
    background: #2f2f2f !important;    
  }
  /* ヘッダー */
  #siteHeader, #siteHeaderInner,#notify_box_container,.infoBox, .program-detail .community-follow, .program-detail .community-follow .btn, #html5-switch-bar,
  .___program-panel___1JWSj, .___header___1JEd2, .___data-grid-layout___1Ls8G, .___header-cell___YrVbm:first-of-type, .___header-cell___YrVbm:nth-of-type(2){
      background:#2f2f2f !important;
      border: inset 0px #222 !important;
  }

  #siteHeader #siteHeaderInner ul li:hover {
    background:#D2691E !important;
  }
  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }
  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer,#notify_box_container.infoBox, .tag, #community-info-block .community-follow .btn, [class*="tag-skin"]{
    background: #2f2f2f !important;
  }
  #siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover,#infoArea{
    background:#D2691E !important;
  }
  
   #siteHeader{
      border-bottom: solid 1px #444 !important;
   }

  #page_header,#notify_box_container, .link-for-switch {
   height:0px;
   background: none repeat scroll 0 0 #1b1b1b !important;
   border-color:#272727!important;
  }
  #announcement{
   background: #ff1100 !important;   
  }
  #commu_info,.title_text,#description_tab_view,#live_header .score_search,.cover {
    color: #eee !important;
  }
  #live_header .score_search .search_word {
    border: 0px solid #555!important;
  }
  #live_header,#description_tab_view,.lebox p,.blbox,#watch_player_top_box,#watch_tab_box,#watch_player_bottom_box,#live-header,#program-info-container,
  .___data-grid-base____LQ5G[data-scroll-mode="y"] > .___body-base___2JUt0{
    padding:5px !important;  
    background: #1b1b1b !important;  
  }

  [class*="header___dEi9q"],
  [class*="body___2YUmE"],
  [class*="ichiba-launcher"],
  [class*="comment-post-panel"],
  [class*="menuboard"]{
    background: #1b1b1b !important;     
  }
  .score_search{
    z-index: 2 !important;  
    margin:15px 0px 0px 500px;
  }
  #watch_title_box, #watch_player_top_box,.cover{
    background: #1b1b1b !important;
    width:1080px !important;
    height:auto !important;
  }
  .cover{
    width:980px !important;
  }
  #watch_title_box{
    z-index: 1 !important;  
  }  
  .title,.meta{
    width:600px !important;
  }
  #page_cover,.textbox,#watch_zapping_box{
    background: #1b1b1b !important;
    border: none!important;
  }

  .title_text{
    text-shadow:#ff0000 1px 1px 2px; 
    font-size:16px !important;
  }

  #watch_tab_box #tab_title_area {
   background: none !important;
  }
  #watch_title_box {
    border: none  !important;
    box-shadow: none  !important;
  }
  #watch_zapping_box {
    border-bottom: none !important;
    padding:5px 0 0 !important;
  }
  #slider_container {
    margin:0 0 20px !important;
  }
  .nicopedia.__nicopedia_loaded,.allegation,a[title="使い方"],a[title="不適切なコメントを通報"],.commu_name,.twitter-share-button{
    background:#444 !important;
    color: #eee !important;
    border: inset 0px #222742 !important;
    text-decoration: none !important;
    padding:5px;
    border-radius: 9px !important;
  }
  .nicopedia.__nicopedia_loaded:hover,.allegation:hover,a[title="使い方"]:hover,a[title="不適切なコメントを通報"]:hover,.commu_name:hover,
  .twitter-share-button:hover{
    color: #eee !important;
    background:#D2691E !important; 
    border: inset 0px #222742 !important;
  }
  .nicopedia.__nicopedia_loaded a{
    text-decoration: none;
  }
  .textArea, #notify_box_container,.infoBox,.lastList,.programList,.time{
    background:#D2691E !important;
    border: inset 0px #222742 !important;
  }
  #header_search_form_input, div#siteHeader div#siteHeaderInner ul.siteHeaderMenuList div.infoBox{
    background:#444 !important; 
  }
  #player-top-container {
    box-shadow: inset 0 0 0 #fff !important;
  }

[class*="program-head"], [class*="page-head"], [class*="site-header"], [class*="watch-page"], [class*="information"], [class*="player-status"], [class*="header-cell"]{
  background:#222 !important; 
}
