@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
 url-prefix(http://userstyles.org/users/) , url-prefix("https://userstyles.org/users/") {
  h1 + ul:not([id]) {
    text-align: center !important;
    padding-right: 3em !important;
    margin-top: 0 !important;
    -moz-appearance: toolbar !important;
  }
  h1 + ul:not([id]):-moz-system-metric(windows-default-theme) {
    -moz-appearance: toolbox !important;
  }
  h1 + ul:not([id]) li {
    margin: 1pt !important;
    display: inline-block !important;
    -moz-appearance: toolbarbutton !important;
  }
  h1 + ul:not([id]) li > a {
    font-size: 80% !important;
    padding: 2pt !important;
    padding-bottom: 3pt !important;
    display: list-item !important;
    list-style-position: inside !important;
    text-decoration: none !important;
    marker-offset: -1.5pt !important;
    color: -moz-fieldtext !important;
    cursor: default !important;
  }
  h1 + ul:not([id]) li > a:first-line {
    margin-top: -2pt !important;
  }
  h1+ul:not([id]) li>a[href*="new"] {
    list-style-image: url(data:image/png;base64,) !important;
  }
  h1+ul:not([id]) li>a[href*="edit"] {
    list-style-image: url(data:image/png;base64,) !important;
  }
  h1+ul:not([id]) li>a[href*="logout"] {
    list-style-image: url(data:image/png;base64,) !important;
  }
  h1+ul:not([id]) li>a[href*="ments/"] {
    list-style-image: url(data:image/png;base64,) !important;
  }
  h1+ul:not([id]) li>a[href*="ments_"] {
    list-style-image: url(data:image/png;base64,) !important;
  }
  h1+ul:not([id]) li>a[href*="forum."] {
    list-style-image: url(data:image/png;base64,) !important;
  }
}
