style.css:
@-moz-document url("https://www.nicovideo.jp/ranking") {
/* ニコ生 */
  /* ニコ生 */
.rankingHeader,
.notice.banner,
.pageLogo,
.endCategory,
.banner.leadBanner.ads,
.footer,
.otherContents,
.top20 .split,
.top20.th,
.td[colspan="11"],
.matrix_float,
.horror_banner {
    display: none !important;
}
/* 文字設定 */
html,
body,
div,
h1,
.h3,
h4,
h5,
h6,
p,
blockquote,
pre,
table,
th,
tr,
td,
ul,
ol,
li,
dl,
dt,
dd,
form,
fieldset,
legend,
hr,
span,
a,
img,
strong,
em,
ins,
del,
cite,
abbr,
acronym,
sup,
sub,
dfn,
code,
var,
samp,
kbd,
b,
i {
    font-weight: 700;
    font-family: 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #eee !important;
    border: inset 0px #222742 !important;
}
html,
body,
.siteHeader,
.header,
.column.main,
.inner,
.top20 .bg_grade_1,
.top20 .uadlevel1,
.top20 .uadlevel2,
.top20 .uadlevel3,
.top20 .uadlevel4,
.top20 .uadlevel5 {
    background: #1b1b1b!important;
    border: none!important;
    color: #fff !important;
    border-width: 0px !important;
    border: inset 0px #222742 !important;
}
table,
td,
tr,
.column1024-0.table,
.top20 .bg_grade_2,
.top20 .uadlevel6,
.top20 .uadlevel7,
.top20 .uadlevel8,
.top20 .uadlevel9,
.top20 .uadlevel10,
.top20 .bg_grade_2,
.top20 .uadlevel6,
.top20 .uadlevel7,
.top20 .uadlevel8,
.top20 .uadlevel9,
.top20 .uadlevel10,
.top20 .bg_grade_0 {
    border: none!important;
    border: inset 0px #222742 !important;
    border-width: 0px !important;
}
table,
th,
td {
    border: none!important;
}
/* ヘッダー */
#siteHeader,
#siteHeaderInner,
li.videoPlayback.active a,
li.comment a,
li.performance a,
li.imageQuality a,
li.videoPlayback a,
.videoSearchOption .tab {
    background: #2f2f2f !important;
    border: inset 0px #222742 !important;
}

#siteHeader #siteHeaderInner ul li:hover,
li.videoPlayback.active a:hover,
li.comment a:hover,
li.performance a:hover,
li.imageQuality a:hover,
li.videoPlayback a:hover {
    background: #D2691E !important;
}
#siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color: #fff !important;
    border: inset 0px #222742 !important;
}
#siteHeaderRightMenuContainer,
#siteHeaderLeftMenuContainer,
.nicolib-SuggestSearch-suggestions.suggestBox,
.nicolib-SuggestSearch-suggestion-list.suggestBoxInner {
    background: #2f2f2f !important;
}
#siteHeaderRightMenuContainer a:hover,
#siteHeaderLeftMenuContainer a:hover,
.videoSearchOption .active,
.videoSearchOption .tab:hover,
.category_row li a:hover,
ul.child_category_group li.child_category a:hover {
    background: #D2691E !important;
}
#siteHeader {
    border-bottom: solid 1px #444 !important;
}
input[type="text"] {
    color: #fff !important;
    background: #333 !important;
    border: inset 0px #222742 !important;
}
.videoSearchOption .tab,
.category_row li a,
.inputText {
    border: solid 1px #444 !important;
}

#page_header {
    background: none repeat scroll 0 0 #111 !important;
    border-color: #272727!important;
}
.top20 .itemTitle a:link {
    color: #eee !important;
    text-shadow: #595cce 1px 1px 2px;
}
.top20 .bg_grade_0,
.column1024-0 table {
    border-collapse: separate !important;
    border-spacing: 0px !important;
}
.itemTitle,
.itemData,
.item,
.matrix video,
section.matrix .item .itemContent,
.RankingBaseItem .Card-link:visited:not(:hover) .Card-title,
.RankingBaseItem .Card-title,
.Card-title,
.Card-link,
.Card-link:active,
.Card-link:visited,
.RankingBaseItem,
.RankingGenreListContainer,
.RankingGenreListContainer-item {
    background: #222;
}
.category_row li.stack .trigger,
.category_row li a,
ul.child_category_group li.child_category a,
section.matrix .item .itemData dl dt,
section.matrix .item .itemData,
section.matrix li.item_row .uadlevel1 .item,
section.matrix li.item_row .uadlevel2 .item,
section.matrix li.item_row .uadlevel3 .item,
section.matrix li.item_row .uadlevel4 .item,
section.matrix li.item_row .uadlevel5 .item,
section.matrix .item .itemContent {
    background: #333 !important;
}
.category_row li.stack .trigger:hover,
.category_row li a:hover,
ul.child_category_group li.child_category a:hover {
    background: #555;
}
}
