style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("seiga.nicovideo.jp") {
  #top_seiga_ad,#top_event_box,#top_ads_box,#top_calendar_box,.ads_300x60,#seiga_pc_prtext,#sg_footer,#ad_728_90,#top_blomaga_box,#logo,#ad_468_60,.sg_global_bar.cfix,
  #top_seigamenu_area,#bk_footer,#ichiba_placeholder,#watch_ad_img,.social_ui,.box.info_box,#ads_pc_seiga_footer, #footer,#mg_notice,.type1,#bk_top_carousel,.banners,.push_contents{
    display: none !important;
  }
  /* 文字設定 */
  html,body,div,h1,h2,h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
  }

  html, body, .content_area,#top_info_box,.main_content,#wrapper,#series,.watch_main_detail.clearfix,.popular_tag_bg{
    background: #1b1b1b !important;
    border: none!important;
    box-shadow: 0px;
  }
  .title,.summary_comment,.pop_fukidashi_container,.pop_fukidashi,.rank_illust.cfix h3,.rank_manga.cfix h3,.rank_book.cfix h3,#top_seigamenu_area,
  #siteHeaderRightMenuContainer,#siteHeaderLeftMenuContainer,.series,.bar_title,.balloon.normal.bottom,.head{
    color:#fff !important;
    background: #0d0d0d !important;
  }
  .content_area_inner.cfix,.item a,.rank_area_inner,.my_illust.my_area,.my_area_inner,.my_manga.my_area,.my_book.my_area,#header,#top_pickup_area,.contents_list,
  .list_item.middle a,.event_box,.rotate_indicator,.rank_innerbox,.top_illust_tag,.menu_block a,.menu_block,.content_area_inner.clearfix,.mg_comment,.rank_list,.content_area_inner,
  .menu,.type2,type2 a, h4 a,.body.js_top_campaign,.related_link.category_all.fukidashi_0,.information,.ranking{
    background: #171717 !important;    
  }
  #bar_search,.rank_item.cfix:hover{
    color: #eee !important;
    background:#333 !important;
    border: inset 0px #222742 !important;
  }
  /* ヘッダー */
  #siteHeader, #siteHeaderInner{
      background:#222742 !important;
      border: inset 0px #222742 !important;
  }

  #siteHeader #siteHeaderInner ul li:hover {
    background:#595cce !important;
  }
  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }

  #siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover,#infoArea{
    background:#595cce !important;
  }
  #page_header {
   background: none repeat scroll 0 0 #111 !important;
   border-color:#272727!important;
  }

  #sg_global_navi{
      margin:-35px 100px 0px 200px !important;  
  }

  .title_text{
    text-shadow:#595cce 1px 1px 2px; 
  }

  .seigamenu_illust,.seigamenu_manga,.seigamenu_book,.sg_navi_illust,.sg_navi_manga,.sg_navi_book,.more_link_txt a, .more_link_list, .more_rank_button a,
  .locked_tag managed_tag a,.bk_series_count,.more a,.tag a,.label_list a, .r18 a,.ui_select.js_select_ranking.pay.selected, .ui_select.js_select_ranking.free,
  .description a{
    background:#222742 !important;
    color: #eee !important;
    border: inset 0px #222742 !important;
    text-decoration: none;
    padding:7px;
  }
  
  .seigamenu_illust:hover,.seigamenu_manga:hover,.seigamenu_book:hover,.sg_navi_top.active,.sg_navi_illust:hover,.sg_navi_manga:hover,.sg_navi_book:hover,
  .more_link_txt a:hover, .more_rank_button a:hover,.more a:hover,.tag a:hover,.menu_block a:hover, .r18 a:hover,.ui_select.js_select_ranking.pay.selected:hover, 
    .ui_select.js_select_ranking.free:hover{
    color: #eee !important;
    background:#595cce !important; 
    border: inset 0px #222742 !important;
  }

}
