style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('slack.com') {
  
  html *{
  }
  
  #col_channels_bg, #col_channels, #team_menu, span.topic,
  #monkey_scroll_bar, #col_flex, #details_toggle, .star_channel,
  #search_container,#recent_mentions_toggle,#stars_toggle,
  #channel_members_toggle{
   display:none !important;
  }
  #client-ui.flex_pane_showing.details_showing #messages_container{
   margin-left: 0;
    margin-right:0;
 }
  #footer{
    left:0;
    width:100%;
  }
  
  #channel_header{
    margin-right:0;
  }
  #msgs_div, .msgs_holder{
   max-width: 350px;
  }
  #header{
    min-width: auto;
  }
  .feature_flexpane_rework #channel_header{
    margin-right:40px;
  }
}
