style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://www.v2ex.com/), regexp(".*://.*v2ex.com/.*") {

a.count_livid {
  visibility: hidden;
}
.votes {
  visibility: hidden;
}
.small {
  visibility: hidden;
}
.box .cell table strong {
  visibility: hidden;
}

span.fade.small {
  visibility: hidden;
/* 首页 新主题的属性 */
}

td span.fade {
  visibility: visible;
/* 个人页 系统提醒里的链接 */
}



.cell {
  padding: 10px;
}

.gray , .fr {
  visibility: hidden
}
.dark {
  visibility: hidden
}

.inner strong {
  visibility: hidden
/* 在线人数 */
}
.box .inner strong {
  visibility: visible
/* n条未读提醒 */
}


}
