style.css:
@-moz-document domain("facebook.com") {
	*[dir=rtl],
	html[dir=rtl] > body,
	div[dir=rtl],
	span[dir=rtl],
	.webMessengerMessageGroup ._38 p,
	._6nm,
	html[dir=rtl] *	{
		font-family: Jameel Noori Nastaleeq, Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 150% !important;
		line-height: 155% !important;
	}
}

@-moz-document domain("twitter.com") {
    *[dir=rtl],
    div[dir=rtl],
    span[dir=rtl],
    .tweet-text-rtl {
		font-family: Jameel Noori Nastaleeq, Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 125%;
		line-height: 145%;
    }
}

@-moz-document domain("ur.wikipedia.org") {
    *[dir=rtl],
    html[dir=rtl] > body,
    div[dir=rtl],
    span[dir=rtl],
    html[dir=rtl] * {
		font-family: Jameel Noori Nastaleeq, Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 105%;
    }
}
