}
blockquote.testimonial {
  -moz-border-radius: 10px;
  -webkit-border-radius: 10px;
  background: #587B8C;
  border-radius: 10px;
  border: 0;
  color: #0099E6;
  font-family: Georgia, serif;
  font-style: italic;
  margin: 0;
  padding: 10px 50px;
  position: relative;
}blockquote.testimonial p {
  color: #E7DEDE;
  font-family: Georgia, serif;
  font-style: italic;
  font-size: 1em;
  line-height: 1.3;
}div.testimonial-arrow-down {
  border-left: 15px solid transparent;
  border-right: 15px solid transparent;
  border-top: 15px solid #587B8C;
  height: 0;
  margin: 0 0 0 25px;
  width: 0;
}div.testimonial-author strong {
  color: #C7CCCF;
  display: block;
  padding-top: 7px;
}.tfoot a:visited, .tfoot_avisited {
  color: #B2B2B5;
}
.thead {
  color: #CDCDCD;
  font: bold 11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
  background-image: -webkit-gradient(linear,0.00% 72.26%,100.00% 27.74%,color-stop(0%,rgba(16,96,144,0.95)),color-stop(100%,rgba(0,55,106,1.00)));
  background-image: -webkit-linear-gradient(24deg,rgba(16,96,144,0.95) 0,rgba(0,55,106,1.00) 100%);
  background-image: linear-gradient(66deg,rgba(16,96,144,0.95) 0,rgba(0,55,106,1.00) 100%);
  border-top-right-radius: 3px;
  border-top-left-radius: 3px;
}.vbmenu_control a:link, .vbmenu_control_alink {
  color: #513C3C;
  text-decoration: none;
}
}
