style.css:
@-moz-document domain("deezer.com") {
html,body, [class="is-entered.player-full"]
{
    background-color:#2d2d3d!important; 
    background-image:none!important;
    color:lightgray!important;
}
  [class="page-main"]{background-color:inherit!important;}
  
[class="page-main"] *:not(span):not(button):not([class="dropdown-menu pull-right"]):not([class="meta_triangle"]):not([class="banner-header-bg"]):not([class="thumbnail-wrapper"]):not([class="view-more-container"]):not([class*="app_disabled"]):not([class*="thumbnail"]):not([class*="action"]):not([class*="icon"]):not([class*="title"]):not([class*="active"]):not([class*="flow"]):not([class*="datagrid-label"]),
  #livebar *:not([class*="icon"])
  {
    background-color:inherit!important; 
    color:white!important;
    font-weight: bold !important;
    border-color:gray !important;
  }

/*
*/
  buttonspan,span[class*="label"],span[class="app-name"],button{
    color:inherit!important;
    background-color:inherit!important; 
    background-image:none!important;
}
  

  [class*="icon"]:not([class^="rounded-icon"]):not([class*="icon-shuffle"]):not([class*="icon-repeat"]):not([class*="icon-prev"]):not([class*="icon-love"]),
  .page-electron-topbar,
  [class="is-entered.player-full"]
  {
    background-color:transparent!important; 
    color:lightgray!important;
  }

  .page-player>:not([class="track-seekbar"]),[class="marquee-content"]
{
    background-color:#101010!important; 
    background-image:none!important;
    color:#fff!important;
}


  #page_panels *:not([class*="checkbox"]):not([class*="slider"]):not([class*="icon"]):not([class*="action"]), [class*="modal-dialog"] *, [class*="context"] *, #livebar_content,
  [class="queuelist-content"]
  {
    background-color:#53535C!important; 
    color:lightgray!important;
    font-weight: bold !important;
  }

  [id*="livemenu"] *:not(span), .search_filter, [class="dropdown-menu pull-right"]
  {
    background:none!important;
    background-color:#53535C!important; 
    color:lightgray!important;
    font-weight: bold !important;
    border-color:lightgray !important; /* lightgray */
    border-width:0px!important;
    border-style:none !important;
  }

  THEAD > TR:first-child
  {
    font-size: 18px !important;
  }

  button[id*="modal"],input
  {
    border-width:1px!important;
    border-style:solid !important;
    border-color:lightgray !important;
  }
  
  [class*="control"]
  {
    padding-left:0px!important;
    padding-right:0px!important;
    padding-top:0px!important;
    padding-bottom:0px!important;
  }

  
  
  [class*="datagrid"] *:not([class*="icon"]):not(span):not(button), [class="suggest-column column-artist"] * {
    background-color:inherit!important;
    color:silver;
    border-width:1px!important;
    border-style:none !important;
    border-color:white !important;
 }

  
  
  
  div[class="catalog-headings"] *   { color:white!important; font-weight: bold !important; }
  [class="song"] *,tr[class="song paused"] *,[class*="datagrid-label"]   { color:silver; font-weight: bold !important; }
  [class="song unavailable"] *   { color:#696969!important; font-style:italic !important; }
  .datagrid-row { position:inherit; background-color:inherit!important;}

	/*
