  */


  div[class="datagrid-row song active"]>:not(button):not([class="datagrid-cell datagrid-cell-action"]) 
	{ 
		/*
    color:#B0C0F5!important; font-weight: bold !important; 
		filter: saturate(100%) invert(0%) drop-shadow(0 0 0 red) brightness(130%) contrast(100%);
    */
