  #ads_banner_top,.ads-bottom.ads,.abp-banner-container,.ads-top.ads,.conversion-entrypoints,
  [class="popper-wrapper topbar-entrypoints"] 
  {
      display: none !important;
  }  


  #page_content {
    min-height: 100% !important;
  }
  
  ::-webkit-scrollbar {width: 8px;}
  ::-webkit-scrollbar-track {-webkit-box-shadow: inset 0 0 6px #fff; background:#000;}
