style.css:
@-moz-document domain("wx.qq.com") {
.listOperator {
	display: none!important;
}
 
.chatPanel {
	width: 98%!important;
	max-width: 960px;
}
 
.chatPanel .list {
	width: 35%!important;
}
 
.chatPanel .chat {
	margin-left: 0;
}
 
.chatPanel .inputArea .attach a.func {
	float: none!important;
}
 
.chatPanel .chatScorll {
	height: 505px!important;
}
 
.chatPanel .chatInput {
	height: 90px!important;
}
 
.chatPanel .chatOperator {
	height: 125px!important;
}
 
.chatPanel .chatContainer {
	height: 665px!important;
}
 
.me .cloudBody,
.me .cloudContent {
	border: none;
	border-top: none!important;
	border-bottom: none!important;
	border-left: none!important;
	background: #E0E0E0;
	;
}
 
.cloudArrow {
	right: -15px!important;
	top: 16px!important;
	background: none!important;
	border-top: 1px dashed #000;
}
 
.chatPanel .chatInput {
	float: left;
	width: 75%!important;
	margin: 0 0px 0 10px!important;
}
 
a.chatSend {
	float: left!important;
	height: 98px!important;
	width: 9%!important;
}
 
a.chatSend b {
	line-height: 100px!important;
}
 
@media (max-width: 630px) {
	.chatPanel .list {
		width: 65px!important;
	}
	.chatPanel .chat {
		margin-left: 0!important;
	}
	.chatPanel .chatInput {
		width: 68%!important;
	}
	.chatPanel .chatListSearch {
		display: none!important;
	}
	.chatPanel .myProfile .info {
		display: none!important;
	}
	.chatPanel .chatScorll {
		height: auto!important;
	}
	.chatPanel {
		padding: 0!important;
	}
	html,
	body {
		min-height: 200px!important;
		overflow-y: hidden!important;
	}
	.chatPanel .listContentWrap {
		height: 555px!important;
	}
}
}
