style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  /**************
  * General CSS *
  **************/
  body {
    background: url("http://img10.deviantart.net/76f4/i/2014/123/1/f/mega_ampharos___thunder_by_ishmam-d7ebkyy.png");
    background-size: cover;
  }
  
  .ps-room.ps-room-light {
    background: transparent;
    border-color: #4F4D4D;
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.4);
  }
  
  /* Header */
  
  .header {
    background: #FFF;
  }
  
  .header .logo {
    opacity: 0;
  }
  
  .userbar button.icon {
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .userbar button.icon:hover {
    border: 1px solid #111;
    color: #111;
    background: #FFF;
  }
  
  .userbar .username {
    text-shadow: none;
  }
  
  .maintabbarbottom {
    border: none;
    box-shadow: none;
    border-bottom: 1px solid #050026;
    background: #FFF;
  }
  
  .tabbar a.button.notifying {
    border-color: #FF1101;
    color: #FF1101;
    background: #FFD9D8;
    text-shadow: none !important;
  }
  
  .tabbar a.button.notifying:hover {
    border-color: #FF1101;
    color: #FF1101;
    background: #FDCFCD;
    text-shadow: none !important;
  }
  
  .tabbar a.button.subtle-notifying {
    color: #FF1101;
    background: #FFF;
    border: 1px solid #4F4D4D;
  }
  
  .tabbar a.button.subtle-notifying:hover {
    color: #FF1101;
    border: 1px solid #111;
    background: #FFF;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border: 1px solid #050026;
    color: #050026;
    background: #FFF;
    height: 26px;
    margin-top: 1px;
  }
  
  .tabbar a.button {
    margin-left: 5px;
    border-radius: 3px;
    margin-bottom: -50px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
    height: 28px;
  }
  
  .tabbar a.button:hover {
    border: 1px solid #111;
    color: #111;
    background: #FFF;
  }
  
  .tabbar.maintabbar {
    margin-left: 180px;
  }
  
  .tabbar {
    position: absolute;
    bottom: 1px;
    left: 0px;
    right: 0px;
    display: block;
    list-style: outside none none;
    margin: 0px;
    height: 43px;
    text-align: left;
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
    font-size: 10pt;
    line-height: 100%;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 3px;
  }
  .tabbar li:first-child a.button {
    border-top-left-radius: 3px;
  }
    
  /* Header ~end */
  
  /*******************
  * General CSS ~end *
  *******************/
  
  /************
  * Rooms CSS *
  ************/
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(255, 255, 255, 0.5);
    color: #222;
  }
  
  .roomlist a.ilink {
    border-color: #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .roomlist a.ilink:hover {
    border-color: #111;
    color: #111;
    background: #FFF;
  }
  
  .userlist {
    background: rgba(255, 255, 255, 0.9);
    color: #222;
    border-right: 1px solid #4F4D4D;
  }
  
  .userlist li {
    border-bottom: 1px solid #4F4D4D;
  }
  
  .userlist li button:hover {
    background: #EEE;
  }
  
  .chat-log-add {
    background: rgba(255, 255, 255, 0.9);
    color: #222;
    border-top: 1px solid #4F4D4D;
  }
  
  .chat-log {
    background: rgba(255, 255, 255, 0.92);
  }
  
  .infobox {
    background: rgba(255, 255, 255, 0.5);
    border-color: #060127;
  }
  
  /*****************
  * Rooms CSS ~end *
  *****************/
  
  /**********************
  * Mainmenuwrapper CSS *
  **********************/
  
  .menugroup {
    background: rgba(255, 255, 255, 0.75);
    border: 1px solid #050026;
  }
  
  .label {
    color: #050026;
    text-shadow: none;
  }
  
  .menugroup .button {
    background: #F7E080;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
    border-radius: 3px;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.3) inset;
  }
  
  .menugroup .button:hover, .menugroup .button:focus {
    background: #E9CE5F;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active {
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026 !important;
    background: rgba(247, 224, 128, 0.6);
  }
  
  button[name="roomlist"], button[name="finduser"] {
    width: 230px !important;
  }
  
  .select {
    border-radius: 3px;
    background: #F7E080;
    color: #050026;
    border: 1px solid #050026;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.3) inset;
  }
  
  .select:hover, .select:focus {
    background: #E9CE5F;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .select::after {
    color: #050026;
  }
  
  .select:disabled, .select:disabled:hover {
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026 !important;
    background: rgba(247, 224, 128, 0.6);
  }
  
  /* PMs */
  
  .pm-window.news-embed {
    opacity: 0.4;
  }
  
  .pm-window.news-embed:hover {
    opacity: 1;
  }
  
  .pm-window h3 {
    border-color: #000;
    background: #01F5FF;
    color: #000;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    border-color: #000;
    color: #000;
    background: #00E7EF;
  }
  
  .pm-window h3.pm-notifying {
    border-color: #FF1101;
    color: #FF1101;
    background: #FFD9D8;
    text-shadow: none !important;
  }
  
  .pm-window h3.pm-notifying:hover {
    border-color: #FF1101;
    color: #FF1101;
    background: #FDCFCD;
    text-shadow: none !important;
  }
  
  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #000;
  }
  
  .pm-window h3 small {
    color: #333;
  }
  
  .pm-log {
    background: rgba(255, 255, 255, 0.92);
    border-color: #060127;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    border-color: #000;
    background: #01F5FF;
  }
  
  .pm-log-add .textbox {
    box-shadow: none;
    border-color: #000;
  }
  
  /* PMs ~end */
  
  /***************************
  * Mainmenuwrapper CSS ~end *
  ***************************/
  
  /* Teambuilder */
  
  .teamchartbox {
    border-top: 1px solid #4F4D4D;
    background: rgba(255, 255, 255, 0.75);
  }
  
  .setchart, .setchart-nickname {
    background: #FFF;
    border-color: #4F4D4D;
  }
  
  .setchart label, .setchart-nickname label {
    color: #222;
    text-shadow: none;
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .setstats, .setdetails, .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setstats:hover, .setdetails:hover {
    border-color: #4F4D4D;
    box-shadow: none;
    color: #4F4D4D;
  }
  
  .setchart-nickname input:focus, .statform input.inputform:focus, .setstats:focus, .setdetails:focus {
    border-color: #060127;
    box-shadow: none;
    color: #060127;
  }
  
  .setchart input:focus {
    border-color: #060127;
    box-shadow: none;
    color: #FF0400;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #FFF;
    border-color: #4F4D4D;
    color: #222;
    text-shadow: none;
  }
  
  .teambar button {
    background: #FFF;
    border-color: #4F4D4D;
    color: #222;
    text-shadow: none;
  }
  
  .teambar button:hover {
    background: #EEE;
  }
  
  .ps-room.ps-room-light.scrollable button {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .pad ul li button {
    margin-bottom: 2px;
  }
  
  .pad .textbox {
    border: 1px solid #4F4D4D;
    box-shadow: none;
  }
  
  .setmenu button {
    border: none !important;
    background: transparent !important;
    color: #000 !important;
  }
  
  .setmenu button:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D !important;
    color: #000 !important;
    background: #FFF !important;
  }
  
  .teambuilder-results .firstresult a, .teambuilder-results .firstresult a:hover, .teambuilder-results .result a:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .utilichart b {
    color: #FF1101;
  }
  
  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
    color: #555;
  }

  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em, .utilichart .numcol {
    color: #777;
  }
  
  .utilichart .typecol img {
    opacity: 0.85;
  }
  
  .teambuilder-clipboard-container {
    border-color: #FF1101;
  }
  
  .teambuilder-clipboard-data, .teambuilder-clipboard-data:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  /* Teambuilder ~end */
  
  .ps-popup {
    background: #F7E080;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
    border-radius: 3px;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.1) inset, -1px -1px 0px rgba(0, 0, 0, 0.18) inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
  }
  
  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #050026;
  }
  
  .ps-popup a, .ps-popup a.ilink:hover {
    color: #315DFF;
  }
  
  .ps-popup button {
    background-color: #4F4D4D;
    border: 1px solid #050026;
    color: #FFF;
    text-shadow: 0px -1px 0px #050026;
    border-radius: 3px;
  }
  
  .popupmenu h3 {
    color: #050026;
  }
  
  .popupmenu button {
    width: 220px;
    overflow-x: hidden;
    color: #000;
    background: transparent;
    border: none;
    text-shadow: none;
    border-radius: 0px;
  }
  
  .popupmenu button:hover, .popupmenu button.sel:hover {
    border-color: #050026;
    background: rgba(255, 255, 255, 0.25);
  }
  
  .popupmenu button.sel {
    border-color: #050026;
  }
  
  .avatarlist button {
    background-color: transparent;
  }
  
  .avatarlist button:hover {
    border-color: #050026;
    background-color: rgba(255, 255, 255, 0.25);
    box-shadow: none;    
  }
  
  .credits h2 {
    color: #060127;
  }
  
  a.subtle, .credits ul, .credits ul li p {
    color: #0C034A;
  }
  
  a.subtle:hover {
    color: #10045B;
  }
  
  .mainmenufooter {
    bottom: 0px;
    left: 0px;
    padding: 10px;
    border-color: #4F4D4D;
    border-style: solid;
    border-width: 1px 1px 0px 0px;
    border-top-right-radius: 5px;
    background: rgba(255, 255, 255, 0.7);
    color: #222;
    text-shadow: none;
  }
  
  .mainmenufooter a {
    color: #222;
    text-shadow: none;
  }
  
  .mainmenufooter a:hover {
    color: #000;
  }
  
  /* Battle CSS */
  
  .ps-room.ps-room-opaque {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .battle-log {
    border-color: #050026;
    background: rgba(255, 255, 255, 0.5);
  }
  
  .battle-log h2 {
    background: #F7E080;
    border-color: #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .battle-log-add {
    background: #F7E080;
    border-color: #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .battle-log-add .textbox {
    border-color: #050026;
  }
  
  .battle-log .chat strong {
    color: #FF1101;
  }
  
  em[style="color:#445566;display:block;"] {
    color: #FF1101 !important;
  }
  
  .battle-log .rated strong {
    background: #060127;
  }
  
  .battle {
    border-color: #050026;
    background: rgba(255, 255, 255, 0.5) !important;
  }

  .trainer strong {
    color: #050026;  
  }
  
  .messagebar {
    position: absolute;
    display: none;
    bottom: -3px;
    left: 0px;
    color: #CCC;
    border-top-left-radius: 0px;
    background: rgba(0, 0, 0, 0.75) none repeat scroll 0% 0%;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    padding: 2px 6px 0px;
    overflow: hidden;
    width: 250px;
  }
  
  .turn {
    font-size: 13pt;
    font-weight: normal;
    margin: 0px;
    padding: 2px 8px;
    border: 1px solid #050026;
    border-radius: 6px;
    color: #050026;
    background: #FCFAF2 none repeat scroll 0% 0%;
  }
  
  .weather em {
    display: block;
    font-size: 12pt;
  }
  
  .sunweather, .sunnydayweather, .desolatelandweather {
    background: url(http://i.imgur.com/DNMtOWR.gif) scroll left top;
  }

  .rainweather, .raindanceweather, .primordialseaweather {
    background: url(http://i.imgur.com/NQqkU6j.gif) scroll left top;
  }
  
  .deltastreamweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
  }

  .hailweather {
    background: url(http://i.imgur.com/PweinWm.gif) scroll left top;
  }

  .sandstormweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
  }

  .pseudoweather {
    background: url(http://play.pokemonshowdown.com/fx/weather-trickroom.png) scroll left top;
  }
  
  .backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
    background: transparent !important;
  }
  
  .backdrop:before{
    display: block;
    content: "";
    background: transparent !important;
    background-size: cover;
    width: 640px;
    height: 360px;
  }
  
  .background {
    background: transparent !important;
  }
  
  #tooltipwrapper {   
    z-index: 102;
  }
  
  #tooltipwrapper .tooltip {
    border-color: #060127;
    background: #F7E080;
  }
  
  #tooltipwrapper .tooltip h2 {
    border-color: #060127;
    background: rgba(255, 255, 255, 0.7);
    color: #060127;
  }
  
  #tooltipwrapper .tooltip h2 small {
    background: rgba(255, 255, 255, 0.7);
  }
  
  #tooltipwrapper .tooltip p {
    background: rgba(255, 255, 255, 0.55);
    color: #060127;
  }
  
  #tooltipwrapper .tooltip p.section {
    border-top: 1px solid #060127;
  }
  
  .moveselect button, .switchselect button {
    color: #060127;
  }
  
  /* Battle ~end */
  
  .tournament-title {
    background: rgba(255, 255, 255, 0.7);
    border-color: #4F4D4D;
  }
  
  .tournament-status, .tournament-toggle {
    background: rgba(255, 255, 255, 0.8);
    border-color: #4F4D4D;
    color: #000;
  }
  
  .tournament-box {
    border-bottom: 1px solid #4F4D4D;
    border-right: 1px solid #4F4D4D;
  }
  
  .ladder table, .ladder td, .ladder th {
    border: 1px solid #4F4D4D;
  }
  
  .ladder th {
    background: #01F5FF;
    color: #000;
  }
  
  .ladder td {
    background: rgba(255, 255, 255, 0.55);
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
}
