  /* Tested with german SERPs */

@-moz-document domain(search.yahoo.com) {
  object[data*="web"],

  ol {
    counter-reset: pageNum;
  }

  li h3:before {
    content: counter(pageNum) ": ";
    counter-increment: pageNum;
    font-weight: bold;
  }
}
