  }
  
  button:hover, button:focus, .button:hover, .button:focus {
    background-color: #863B2F;
  }
  
  .arrow {
    color: #EFB26C !important;
  }
  
  .icon {
    color: #EFB26C !important;
  }
  
  header {
    background-color:#332222 !important;
  }
  
  h1.logo a {
    background: url("data:image/png;base64,");
    background-size:132px 64px;
  }
  
  form.search .main {
    background-color:#863B2F;
  }
  
  form.search .inner {
    background: none repeat scroll 0 0 #2A1B1B;
  }
  
  form.search .search_type {
    border-right: 1px solid #863B2F;
    color: #986E3F;
  }
  
