    background-color: #332222;
    color: #EFB26C !important;
    width:34px;
    height:46px;
    font-size:1.4em;
    right:0em;
    top:0.75em;
  }
  
  form.search .input_methods {
    color: #EFB26C;
  }
  
  form.search .input_method_button h4 {
    color: #EFB26C;
  }
  
  .panel .buttons button {
    background-color:#332222 !important;
  }
  
