    background-color: #332222;
  }
  
  .tabs dd > a:hover, .tabs .tab-title > a:hover {
    background-color: #863B2F;
  }
  
  table {
    background: none repeat scroll 0 0 #863B2F;
    border: 1px solid #2A1B1B;
  }
  
  table tr th, table tr td {
    color: #EFB26C;
  }
  
  table tr.even, table tr.alt, table tr:nth-of-type(2n) {
    background: none repeat scroll 0 0 #2A1B1B;
  }
  
  #what_is_this {
    color: inherit;
  }
  
  footer .ornament {
    background: none repeat scroll 0 0 #EF7D6C;
    box-shadow: 0 0 10px 5px #986E3F;
  }
  
  #site_notice {
    background-color: #2A1B1B;
    color: #EFB26C;
  }
  
  form.search .input_method_button:hover {
   background-color: #863b2f;
   border-radius: 4px;
  }
  
  .show_more {
    width:2.7em !important;
    height:1.3em !important;
    padding-right: 0.1em;
  }
  
  form.search .submit {
    background: transparent;
  }
  
  form.search .search-form_clear-button {
    background: transparent;
  }
  
