  .discussion_thread .comment_body .discussion_status, .discussion_thread .comment_body .comment_status {
    background: none repeat scroll 0 0 #863B2F;
    border-bottom: 1px solid #986E3F;
    color: #EFB26C;
  }
  
  .discussion_thread .comment_body {
    background: none repeat scroll 0 0 #2A1B1B;
    border: 1px solid #986E3F;
  }
  
  .discussion_thread .comment_body .discussion_about {
    background: none repeat scroll 0 0 #382820;
    border-bottom: 1px solid #986E3F;
    color: #EFB26C;
  }
  
  .discussion-username__forum-flair {
    background-color: #382820;
    border: 1px solid #EFB26C;
    color: #EFB26C;
  }
  
  .pagination a {
    border: 1px solid #EFB26C;
  }
  
  .pagination span.disabled {
    border: 1px solid #999;
    color: #999;
  }
  
  .concept_light-status .concept_light-status_link {
   width:3.2em;  
  }
  
