style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  /**************
  * General CSS *
  **************/
  body {
    background: #C4CCDF !important;
  }
  
  .mainmenuwrapper {
    background: url("http://i.imgur.com/WHU1GM0.png?1") center right no-repeat;
  }
  
  .ps-room.ps-room-light {
    background: transparent;
    border-color: #000;
    z-index: 2;
  }
  
  .textbox {
    border: 1px solid #000;
    box-shadow: none;
    background: #F9F9F9;
  }
  
  .textbox:hover {
    background: #FFF;
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .textbox:focus {
    border-color: #78C2CF;
    box-shadow: 0px 1px 2px #D2D2D2 inset, 0px 0px 5px #C4CCDF;
  }
  
  /* Header CSS */
  
  .header {
    background: rgba(0, 0, 0, 0.6);
  }
  
  .userbar .username {
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
  }
  
  .header button {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 3px;
    padding: 3px 5px;
  }
  
  .header button:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .maintabbarbottom {
    border-color: #000;
    background: #79C5D2;
    box-shadow: 0px 4px 5px rgba(0, 0, 0, 0.45);
    z-index: 3;
  }
  
  .tabbar a.button.notifying {
    background: #4C5A74;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0px -1px 0px #000;
    z-index: 4;
  }

  .tabbar a.button.notifying:hover {
    background: #5E7090;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0px -1px 0px #000;
  }

  .tabbar a.button.subtle-notifying {
    color: #E35F40;
    background:#F9F9F9;
    border: 1px solid #000;
    text-shadow: none;
  }

  .tabbar a.button.subtle-notifying:hover {
    color: #E35F40;
    background: #FFF;
    border: 1px solid #000;
    text-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    z-index: 3;
  }
  
  .tabbar a.button {
    border-color: #000;
    background: #F9F9F9;
    color: #3D3C41;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    border-color: #000;
    background: #EEE;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 3px;
  }
  .tabbar li:first-child a.button {
    border-top-left-radius: 3px;
  }
  
  /* Header CSS ~end */
  
  /* Popup CSS */
  
  .ps-popup {
    border-color: #000;
    background: #4E5259;
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.5) inset, -1px -1px 0px rgba(228, 228, 228, 0.5) inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
    color: #F2F2F2;
    border-radius: 3px;
  }
  
  .popupmenu button {
    width: 220px;
    overflow-x: hidden;
    color: #C4CCDF !important;
    background: none !important;
    border: none !important;
    text-shadow: none !important;
  }
  
  .popupmenu h3 {
    color: #76C2CF;
  }
  
  .popupmenu button.sel {
    border-color: #000;
  }
  
  .popupmenu button:hover, .popupmenu button.sel:hover {
    border: 1px solid #000 !important;
    background: rgba(0, 0, 0, 0.4) !important;
  }
  
  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #F2F2F2;
  }
  
  .ps-popup button {
    border: 1px solid #000;
    background-color: #9AE1EF;
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 3px;
  }
  
  .ps-popup button:hover {
    border: 1px solid #000;
    background-color: #79C5D2;
    color: #3D3C41;
  }
  
  .ps-popup a {
    color: #78C2CF;
  }
  
  .ps-popup a.ilink {
    color: #C4CCDF;
  }
  
  .ps-popup a.ilink:hover {
    color: #B2B9C9;
  }
  
  .avatarlist button {
    border-color: #000;
  }
  
  .avatarlist button:hover {
    border-color: #000;
    background-color: rgba(0, 0, 0, 0.4);
    box-shadow: none;
  }
  
  .credits h2, .credits ul {
    color: #78C2CF;
  }
  
  a.subtle, .credits ul li p {
    color: #C4CCDF;
  }
  
  a.subtle:hover {
    color: #B1B8C9;
  }
  
  .credits li small {
    color: #BBB;
  }
  
  p[style="text-align:center;color:#555555"] {
    color: #BBB !important;
  }
  
  .effect-volume em, .music-volume em {
    color: #BBB;
  }
  
  p.or {
    color: #EEE;
  }
  
  p.or::after, p.or::before {
    color: #999;
  }
  
  /* Popup CSS ~end */
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.4);
  }
  
  /*******************
  * General CSS ~end *
  *******************/
  
  /************
  * Rooms CSS *
  ************/
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(0, 0, 0, 0.6) url("http://i.imgur.com/monrueM.png") right no-repeat;
    color: #FFF;
    text-shadow: 0px -1px 0px #000;
  }
  
  .ps-room.ps-room-light.scrollable button {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 3px;
    padding: 3px 5px;
  }

  .teamlist .team {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: none !important;
    border-radius: 3px;
  }
  
  .ps-room.ps-room-light.scrollable button:hover, .teamlist .team:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .ps-room.ps-room-light.scrollable ul li button {
    margin-bottom: 2px;
  }
  
  .roomlist a.ilink {
    border-color: #000;
    background: #F9F9F9;
    color: #3D3C41;
    text-shadow: none;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.5);
    border-radius: 3px;
  }
  
  .roomlist a.ilink small {
    color: #4C5A74;
  }
  
  .roomlist a.ilink:hover {
    border-color: #000;
    background: #EEE;
    color: #3D3C41;
    box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.5);
  }
  
  .roomlist h2 {
    font-style: italic;
    text-shadow: 0px -1px 0px #000;
  }
  
  .chat-log-add {
    background: rgba(0, 0, 0, 0.6);
    border-top: 1px solid #000;
  }
  
  .chat-log-add .chatbox label {
    text-shadow: 1px 1px #000;
  }
  
  .userlist {
    background: rgba(0, 0, 0, 0.6);
    color: #FFF;
    text-shadow: 1px 1px #000;
    border-right: 1px solid #4E5259;
  }
  
  .userlist li {
    border-bottom: 1px solid #000;
    background: transparent;
  }
  
  .userlist li button {
    background: rgba(0, 0, 0, 0.4);
    text-shadow: 1px 1px #000;
  }
  
  .userlist li em.group {
    color: #C4CCDF;
  }
  
  .userlist li button:hover {
    background: rgba(0, 0, 0, 0.25);
  }
  
  .chat-log {
    background: rgba(255, 255, 255, 0.6);
  }
  
  .chat-log .utilichart {
    background: rgba(0, 0, 0, 0.8);
    color: #FFF !important;
    border-radius: 3px;
  }
  
  .chat-log .chat small {
    color: #333;
  }
  
  .broadcast-blue {
    text-align: center;
    font-family: sans-serif;
    padding: 4px;
    margin: 5px;
    background: #79C5D2;
    border-style: solid;
    border-width: 1px 3px 1px 3px;
    border-color: #000;
    color: #000;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 2px;
  }
  
  .broadcast-blue a {
    color: #000;
  }

  .broadcast-red {
    text-align: center;
    font-family: sans-serif;
    padding: 4px;
    margin: 5px;
    background: #D27A7A;
    border-style: solid;
    border-width: 1px 3px 1px 3px;
    border-color: #000;
    color: #000;
    text-shadow: 0px -1px 0px #DC8989;
    border-radius: 2px;
  }
  
  .broadcast-red a {
    color: #000;
  }

  .broadcast-green {
    text-align: center;
    font-family: sans-serif;
    padding: 4px;
    margin: 5px;
    background: #7DD27A;
    border-style: solid;
    border-width: 1px 3px 1px 3px;
    border-color: #000;
    color: #000;
    text-shadow: 0px -1px 0px #8CDC89;
    border-radius: 2px;  
  }
  
  .broadcast-green a {
    color: #000;
  }
  
  .tournament-title {
    background: #4E5259;
    color: #C4CCDF;
    text-shadow: 0px -1px 0px #000;
    border-color: #000;
  }
  
  .tournament-status, .tournament-toggle {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
  }
  
  .tournament-status:hover, .tournament-toggle:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .tournament-box {
    border-color: #000;
    background: url("http://img10.deviantart.net/5013/i/2013/003/2/9/dark_snow_background_by_grim_s_morrison-d5qayzw.png");
    background-size: cover;
  }
  
  .ladder table, .ladder td, .ladder th {
    border-color: #000;
  }
  
  .ladder th {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
  }
  
  .ladder td {
    background: rgba(0, 0, 0, 0.3);
  }
  
  /*****************
  * Rooms CSS ~end *
  *****************/
  
  /**********************
  * Mainmenuwrapper CSS *
  **********************/
  
  .mainmenuwrapper {
    z-index: 2;
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid #000;
  }
  
  .label {
    color: #FFF;
    text-shadow: none;
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .menugroup button {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 3px;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, .menugroup button:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .menugroup .button:focus {
    background: transparent -moz-linear-gradient(center top , #79C5D2, #9AE1EF);
    background: linear-gradient(to bottom,hsl(189, 50%, 65%),hsl(190, 73%, 77%));
    border-color: #000;
  }
  
  .menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active {
    background: #79C5D2;
    border-color: #000;
    color: #3D3C41;
  }
  
  .select {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.8) inset;
    border-radius: 3px;
  }
  
  .select:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .select:focus {
    background: transparent -moz-linear-gradient(center top , #79C5D2, #9AE1EF);
    border-color: #000;
  }
  
  .select::after {
    color: #3D3C41;
  }
  
  .select:disabled, .select:disabled:hover {
    background: rgba(121, 197, 210, 0.7);
    border-color: #000;
    color: #3D3C41 !important;
    text-shadow: none;
  }
  
  .mainmenufooter a {
    color: #4C5A74;
    text-shadow: none;
  }
  
  .mainmenufooter a:hover {
    color: #2D3543;
  }
  
  .mainmenufooter {
    color: #4C5A74;
    text-shadow: none;
    padding: 10px;
    position: fixed;
    bottom: 0px;
    left: 0px;
    border-color: #000;
    border-style: solid;
    border-width: 1px 1px 0px 0px;
    background: rgba(255, 255, 255, 0.15);
    border-top-right-radius: 5px;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.45);
  }
  
  .pm-window.news-embed {
    opacity: 0.5;
  }
  
  .pm-window.news-embed:hover {
    opacity: 1;
  }
  
  .pm-window.news-embed h3 {
    border-color: #000;
    background: #79C5D2;
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
  
  .pm-window.news-embed .pm-log {
    border-color: #000;
    border-bottom: 1px solid #000;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
  }
  
  .pm-log a {
    color: #4C5A74;
  }
  
  .pm-window.news-embed .closebutton {
    color: #3D3C41;
  }
  
  .pm-window.news-embed .closebutton:hover {
    color: #BC5252;
  }
  
  .pm-window h3.pm-notifying {
    background: #4C5A74;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0px -1px 0px #000;
  }

  .pm-window h3.pm-notifying:hover {
    background: #5E7090;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0px -1px 0px #A23B68;
  }
  
  .pm-window h3 {
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    border-color: #000;
    color: #3D3C41;
    text-shadow: none;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    background: #79C5D2;
    border-color: #000;
    color: #3D3C41;
  }
  
  .pm-window h3 small {
    color: #555;
  }
  
  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #3D3C41;
  }
  
  .pm-window h3 .closebutton:hover, .pm-window h3 .minimizebutton:hover {
    color: #4C5A74;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    border-color: #000;
  }
  
  .pm-log .utilichart {
    background: rgba(0, 0, 0, 0.8);
    color: #FFF !important;
    border-radius: 3px;
  }
  
  .challenge {
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid #000;
    color: #C4CCDF;
  }
  
  .challenge button {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
    border-radius: 3px;
    padding: 3px 5px;
  }
  
  .challenge button:hover {
    background: #79C5D2;
    border-color: #000;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    border-color: #000;
  }
  
  .pm-window.focused .pm-log-add {
    background: #78C2CF;
  }
  
  /***************************
  * Mainmenuwrapper CSS ~end *
  ***************************/
  
  /******************
  * Teambuilder CSS *
  ******************/
  
  .teamchartbox {
    border-top: 1px solid #000;
    background: #4E5259;
  }
  
  .setchart, .setchart-nickname {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
  }
  
  .setchart label, .setchart-nickname label {
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .setdetails, .setstats {
    border: 1px solid #000;
    background: #F9F9F9 !important;
    box-shadow: none;
  }
  
  .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setdetails:hover, .setstats:hover {
    border: 1px solid #000;
    background: #FFF !important;
    box-shadow: none;
  }
  
  .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .setdetails:focus, .setstats:focus {
    border-color: #78C2CF;
    box-shadow: 0px 1px 2px #D2D2D2 inset, 0px 0px 5px #C4CCDF;
  }
  
  .setdetails, .setstats {
    text-shadow: none !important;
    padding: 0px !important;
  }
  
  .setchart .setdetails label, .setchart .setstats label {
    text-shadow: none;
  }
  
  .setmenu {
    top: 2px;
  }
  
  .setmenu button {
    background: none !important;
    border: none !important;
    color: #FFF !important;
    text-shadow: 0px 0px 1px #000 !important;
    padding: 3px 6px !important;
  }
  
  .setmenu button:hover {
    background: rgba(0, 0, 0, 0.4) !important;
    border: 1px solid #000 !important;
    color: #FFF !important;
    text-shadow: 0px 0px 1px #000 !important;
    padding: 2px 5px !important;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #9AE1EF, #79C5D2);
    background: linear-gradient(to bottom,hsl(190, 73%, 77%),hsl(189, 50%, 65%));
    color: #3D3C41;
    text-shadow: 0px -1px 0px #89CFDC;
  }
  
  .teambar button {
    padding: 0px !important;
    border-radius: 0px !important;
  }
  
  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: #79C5D2 !important;
    border: 1px solid #000;
  }
  
  .teambuilder-results {
    background: transparent;
  }
  
  .utilichart .movenamecol, .utilichart .pokemonnamecol, .utilichart .namecol {
    color: #FFF;
  }
  
  .utilichart .numcol {
    color: #78C2CF !important;
  }
  
  .teambuilder-results .firstresult a, .teambuilder-results .result a:hover {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid #000;
  }
  
  .utilichart b {
    color: #78C2CF;
  }
  
  .utilichart .twoabilitycol, .utilichart .abilitycol, .utilichart .col {
    color: #DDD;
    text-shadow: none;
  }
  
  .utilichart .statcol em, .utilichart .bstcol em, .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em {
    color: #C4CCDF;
    text-shadow: none;
  }
  
  .utilichart .typecol img {
    opacity: 0.85;
  }
  
  .teambuilder-clipboard-container {
    border-color: #7BBFD4;
  }
  
