@-moz-document regexp("chrome-extension://[^/]+/edit.html.*"), regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/edit.html*") {
/* Version 2.2.7 */

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(http://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
}

@media (max-width: 740px){
#basic-info > *:not(:last-child) {
margin: 0!important;
}
}

label{
color: #555!important;
}

input[type="checkbox"]:not(#enabled){
-webkit-appearance: none!important;
background: #3f3f3f url(data:image/png;base64,) center no-repeat!important;
border-radius: 3px!important;
height: 24px!important; width: 33px!important;
outline: none!important;
}

input[type="checkbox"]:checked:not(#enabled){
background: #2E6CB1 url(data:image/png;base64,) center no-repeat!important;
}

select, #options [type="number"]{
color: #555!important;
-webkit-appearance: menu!important;
border: 1px solid rgba(0,0,0,0.2)!important;
padding: 5px!important;
background: white!important;
border-radius: 3px!important;
outline: none!important;
width: 120px!important;
}

.option.aligned{
display: block!important;
height: 30px!important;
margin-top: 10px!important;
}

.option.aligned *{
display: block!important;
float: left!important;
}

.option.aligned label{
margin-top: 8px!important;
}

.option input ~ label{
position: relative; top: -11px!important;
}

*{
font-family: 'Roboto'!important;
}

/* Hide Elements*/

#to-mozilla-help, #enabled-label,#mozilla-format-heading{
display: none!important;
}

#header{
background: #eee!important;
border-right: 1px solid rgba(0,0,0,0.15)!important;
box-shadow: none!important;
width: 240px!important;
padding: 15px!important;
}

section#basic-info{
padding-bottom: 25px!important;
}

#basic-info-name{
float: left!important;
}

#url{
position: absolute!important; top: 18px!important; right: 15px!important;
}

#basic-info-enabled{
float: left!important;
}

#enabled{
-webkit-appearance: none!important;
background: #aaa url(data:image/png;base64,) center no-repeat!important;
height: 31px!important; width: 40px!important;
border-radius: 0 3px 3px 0;
margin: 0!important;
outline: none!important;
}

#enabled:hover{
background-color: #888!important;
}

#enabled:checked:hover{
background-color: #3278C7!important;
}

#enabled:checked{
background: #2E6CB1 url(data:image/png;base64,) center no-repeat!important;
}

input#name{
color: #555!important;
-webkit-appearance: none!important;
-webkit-background-clip: padding;
border: 1px solid rgba(0,0,0,0.17)!important;
border-right: none!important;
border-radius: 3px 0 0 3px!important;
padding: 7px 3px 7px 7px!important;
width: 201px!important;
margin-left: -1px!important;
transition: 0.4s!important;
}

input#name:focus{
border: 1px solid #2E6CB1!important;
box-shadow: 0 0 5px #2E6CB1!important;
outline: none!important;
}

button{
outline: none!important;
}

#actions > *{
margin: 0 auto!important;
}

#actions > div{
overflow: hidden!important;
}

#actions > div:nth-child(1){
margin-bottom: 15px!important;
}

#actions > div button{
color: white!important;
-webkit-appearance: none!important;
background-color: #2E6CB1!important;
background-image: url('data:image/png;base64,')!important;
background-position: center 0!important;
border: none!important;
height: 40px!important;
float: left!important;
padding: 0!important; margin: 0!important;
border-right: 1px solid rgba(0,0,0,0.2)!important;
font-size: 0!important;
}

#actions > div button:hover{
background-color: #337ED4!important;
}

#actions > div:nth-child(1) button{
width: calc(240px / 3)!important;
}

#actions > div:nth-child(2) button{
width: calc(240px / 2)!important;
}

#actions #save-button, #actions #from-mozilla{
border-radius: 3px 0 0 3px!important;
}

#actions #cancel-button, #actions #to-mozilla{
border-radius: 0 3px 3px 0!important;
border-right: none!important;
}

#actions #save-button{
background-position: center 0!important;
}

#actions #beautify{
background-position: center -80px!important;
}

#actions #cancel-button{
background-position: center -119px!important;
}

#actions #from-mozilla{
background-position: center -160px!important;
}

#actions #to-mozilla{
background-position: center -200px!important;
}

h1, h2{
font-weight: normal!important;
color: #5f5f5f!important;
border-bottom: 1px solid #ddd!important;
padding-bottom: 3px!important;
}

section:not(#lint){
display: block!important;
clear: both!important;
}

.dirty #actions #save-button{
background-position: center -40px!important;
box-shadow: 0 1px 0 rgba(255, 255, 255, 0.2) inset;
background-color: #FF123D!important;
}

section#sections{
width: calc(100% - 271px)!important;
float: right;
}

#sections > div:not(:first-of-type){
border-top: 1px solid #ccc!important;
}

#sections > div{
position: relative!important;
margin: 0!important; padding: 0!important;
}

#sections h2{
display: none!important;
}

#sections > div > label{
background: #FFF!important;
border-bottom: 1px solid #ddd!important;
color: #777!important;
font-size: 20px!important;
padding: 10px 15px!important;
display: block!important;
}

.applies-to{
display:block!important;
padding: 0 15px 15px 10px!important;
}

.applies-to ul{
margin-top: 10px!important;
}

.applies-to ~ button{
color: #555!important;
background: rgba(0,0,0,0.2)!important;
border: none!important;
border-radius: 0!important;
padding: 8px!important;
position: relative!important; bottom: 10px!important; left: 15px!important;
margin-right: -6px!important;
width: calc(100% / 3 - 9px)!important;
}

.applies-to ~ button:hover{
background: rgba(0,0,0,0.3)!important;
}

button.remove-section{
border-radius: 3px 0 0 3px!important;
margin-right: -7px!important;
}

button.add-section{
margin-right: -7px!important;
}

#sections > div:only-of-type .add-section{
border-radius: 3px 0 0 3px!important;
width: calc(100% / 2 - 14px)!important;
margin-left: 0!important;
}

#sections > div:only-of-type .beautify-section{
width: calc(100% / 2 - 14px)!important;
}

button.beautify-section{
border-radius: 0 3px 3px 0!important;
border-right: 0!important;
}

.applies-to label{
color: #555!important;
font-size: 16px!important;
margin: 0 0 0 8px!important;
}

.applies-type{
color: #555!important;
border: 1px solid #ccc!important;
height: 30px!important;
border-radius: 2px 0 0 2px!important;
padding: 0 4px!important;
}

.applies-value{
color: #555!important;
height: 30px!important;
border: 1px solid #ccc!important;
border-width: 1px 0 1px 0!important;
padding-left: 5px!important;
margin-left: 0!important;
}

.applies-to-list button{
color: #555!important;
background: #ccc!important;
height: 30px!important;
}

.applies-to-list button:hover{
background: #aaa!important;
}

.applies-to-everything{
margin-left: 11px!important;
}

.applies-to-everything > button{
float: right!important;
}

.remove-applies-to{
padding: 0 10px!important;
border: none!important;
border-radius: 0 3px 3px 0!important;
}

.add-applies-to{
position: relative!important; z-index: 100!important;
border: none!important;
border-left: 1px solid rgba(0,0,0,0.1)!important;
padding: 0 10px!important;
border-radius: 0 3px 3px 0!important;
}

.remove-applies-to ~ .add-applies-to{
margin-left: -2px!important;
}

.CodeMirror{
border-width: 0 0 1px 0!important;
outline: none!important;
color: #555!important;
}

.CodeMirror:focus:before{
position: absolute!important; top: 0; left: 0;
content: ''!important;
height: 10px; width: 100%;
background: linear-gradient(rgba(0,0,0,0.2), rgba(255,255,255,0));
z-index: 100!important;
}

@media (max-width: 740px){
#basic-info > *:not(:last-child) {
margin: 0!important;
}
}

@media (max-width: 740px){
#header {
box-sizing: border-box!important;
position: static!important;
width: 100%!important;
height: 100%!important;
}

#header > *{
width: 271px!important;
}

#header > section > h2{
display: block!important;
}

#options{
-webkit-column-count: 1!important;
}

#sections{
width: 100%!important;
padding-left: 241px!important;
}
}

