@-moz-document regexp("chrome-extension://[^/]+/manage.html"), regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/manage.html*") {
@keyframes fadein {
  0%, 100% {
    opacity: 0;
    visibility: hidden;
  }

  20%,40%,60%,80% {
    opacity: 1;
    visibility: visible;
  }

}


* {
    font-family: "Roboto", sans-serif;
}

#header {
    background: #eee!important;
    border-right: 1px solid rgba(0, 0, 0, 0.15)!important;
    box-shadow: none!important;
    width: 240px!important;
    padding: 15px!important;
    height: 100%!important;
}

h1,
h2 {
    font-weight: normal!important;
    color: #5f5f5f!important;
    border-bottom: 1px solid #d7d7d7!important;
    padding-bottom: 5px!important;
}

input{
margin-left: 0!important;
}

label{
color: #555!important;
}

input ~ label{
margin-left: 7px!important;
}

#manage-text{
margin-bottom: 50px!important;
}

fieldset {
    border: none!important;
    margin: 0 0 10px 0!important;
    padding: 30px 0 10px 0!important;
    position: relative!important;
}

fieldset:before{
content: 'Filters'!important;
position: absolute!important; top: -3px!important; left: -2px!important;
font-size: 20px!important;
font-weight: normal!important;
color: #5f5f5f!important;
border-bottom: 1px solid #d7d7d7!important;
padding-bottom: 5px!important;
width: 100%!important;
}

legend#filters{
display: none!important;
}

fieldset > div> #search{
  color: #555!important;
  -webkit-appearance: menu!important;
  border: 1px solid rgba(0,0,0,0.2)!important;
  margin-top: 10px;
  padding: 5px!important;
  background: white!important;
  border-radius: 3px!important;
  height: 35px!important;
  outline: none!important;
  width: 100%!important;
}

input[type="checkbox"] {
    -webkit-appearance: none!important;
    background: #3f3f3f url(data:image/png;base64,) center no-repeat!important;
    border-radius: 3px!important;
    height: 24px!important;
    width: 33px!important;
    outline: none!important;
}

input[type="checkbox"]:checked {
    background: #2e6cb1 url(data:image/png;base64,) center no-repeat!important;
}

input[type="checkbox"] ~ label {
    position: relative!important;
    top: -10px!important;
}

#show-badge-label,
#stylesFirst-label {
    line-height: 20px!important;
}

#installed {
    width: calc(100% - 271px)!important;
    float: right!important;
}

[style-id] {
    border-top: 1px solid #ddd!important;
    margin: 0 auto!important;
    position: relative!important;
}

[style-id]:only-of-type, [style-id]:first-child {
border-top: none!important;
}

#options {
    position: relative!important;
    top: 15px!important;
    clear: both!important;
}

#check-all-updates , #apply-all-updates{
font-size: 0!important;
    -webkit-appearance: none!important;
    background: #2E6CB1 url('data:image/png;base64,') center -240px!important;
    border: none;
    border-right: 1px solid rgba(0,0,0,0.2)!important;
    border-radius: 0 3px 3px 0!important;
    height: 40px;
    width: 120px;
    padding: 0!important;
    margin: 0!important;
    float: right!important;
    outline: none!important;
}

#apply-all-updates{
background: #FF123D url('data:image/png;base64,') center -280px!important;
}

#add-style-label {
    color: white!important;
    -webkit-appearance: none!important;
    background: #2E6CB1 url('data:image/png;base64,') center -320px!important;
    box-shadow: -1px 0 0 rgba(0,0,0,0.2) inset;
    border:none;
    border-radius: 3px 0 0 3px !important;
    height: 40px;
    width: 120px;
    padding: 0!important;
    margin: 0!important;
    float: left!important;
    outline: none!important;
    font-size: 0!important;
}
#check-all-updates:hover,
#add-style-label:hover {
    background-color: #3278C7!important;
}
a[href="edit.html"] {
    height: 40px!important;
    width: 120px!important;
    float: left!important;
}

#header > p:nth-child(5){
position: absolute!important;
}

fieldset ~ p{
margin: 0!important;
}

fieldset ~ p{
position: relative!important; z-index: 100000!important;
}

.actions button {
    -webkit-appearance: none!important;
    background: none!important;
    border: 1px solid #ccc;
    border-radius: 3px!important;
    height: 35px;
    width: 120px;
    padding: 0!important;
    outline: none!important;
    -webkit-background-clip: padding!important;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) inset;
}

.actions button:hover {
    background: #f1f1f1!important;
    transition: 0.15s!important;
}

div span.update-note{
background: rgba(42,91,225, 0.95)!important;
border-radius: 3px!important;
box-shadow: 0 0 10px rgba(0,0,0,0.8)!important;
color: #fff!important;
display: block!important;
font-size: 18px!important;
height:35px!important;
line-height: 25px!important;
opacity:0!important;
padding-top: 10px!important;
position: absolute!important; top: 50%!important; left: 50%!important;
text-align: center!important;
-webkit-transform: translate(-50%, -50%)!important;
width: 200px!important;
pointer-events: none;
}

div.no-update span.update-note{
animation: fadein 2s ease-in-out 0s 1;    
}

div .check-update{
display: inline-block!important;
}

div.no-update .check-update{
display: inline-block!important;
opacity: 0.5!important;
pointer-events: none;
}

#update-all-no-updates{
position: absolute!important;top: -13px!important;
}

#manage-text{
padding-top: 20px!important;
}

#analyticsEnabled-label{
line-height: 20px!important;
}

@media (max-width: 900px){
#header {
box-sizing: border-box!important;
position: static!important;
width: 100%!important;
height: auto!important;
}

#installed{
width: 100%!important;
}
}
