style.css:
@-moz-document regexp("http(s|)://getpocket.com/a/(queue|favorites|archive)/.*") {
@media all and (max-width: 30em) {
.toolbar_queue_divider,
.page_queue_list .item .hasTags {
    display: none;
}
#page .pkt-nav {
    top: -10px;
    left: -12px;
    width: 104%;
    height: 40px;
    text-align: center;
    background: #F4F4F6 none repeat scroll 0% 0%;
    overflow: visible;
    z-index: 1000;
}
.queue_list {
    list-style: outside none none;
    position: relative;
    top: -20px;
}
.item_content {
    min-height: 20px;
}
.item .title {
    font-size: 1.1em;
    padding: 10px 7px 0.2em 67px;
}
.item .buttons {
    list-style: outside none none;
    margin: 0px;
    padding: 0px;
    position: absolute;
    bottom: 0em;
    left: 150px;
    z-index: 10;
}
.item .sub {
    margin: 0em 100px 0.5em 67px;
    padding: 0px;
}
.item .thumb {
    left: 0px;
    right: auto;
    width: 60px;
}
a.original_url {
  overflow: visible !important;
}
a.original_url::before {
  display: block;
  content: "";
  width: 1157px;
  height: 287px;
  position: absolute;
  top: -257px;
  left: -328px;
}
}
}
