style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {

  .ps-room.ps-room-light.scrollable ul li button {
    border-radius: 3px;
    border: 1px solid #000;
    margin-bottom: 2px;
    background: #181818;
    color: #F2F2F2;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .ps-room.ps-room-light.scrollable ul li button:hover, .ps-room.ps-room-light.scrollable ul li button:focus {
    color: #0094FF;
  }
  
  button[name="edit"], button[name="delete"] {
    margin-left: 2px;
  }

  .teamchartbox ::-moz-selection {
    background: #000 !important;
    color: #0094FF !important;
  }

  .teambuilder-results h3:after, .teambuilder-results .utilichart h3:after, .setchart-nickname:after, .statform:after, .detailsform:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/wleX36y.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3, .statform, .detailsform {
    position: relative; 
    border-top-left-radius: 0px;
    margin-top: 5px;    
  }

  .setchart-nickname { 
    border-top-left-radius: 0px;
    border-top-right-radius: 2px;
    margin-top: 2px;
  }

  .teamchartbox {
    border-top: none;
    font-family: sans-serif;
  }

  .teambuilder-pokemon-import .textbox {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .teambuilder-pokemon-import .textbox:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .teambuilder-pokemon-import .textbox:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    border-color: #181818;
    background: #181818;
    color: #FFF;
    text-shadow: 1px 1px #000;
    box-shadow: none;
    margin: 10px 0px 5px 0px;
    font-family: sans-serif;
  }

  .setchart {
    border-color: #000;
    background: #181818;
    background-repeat: no-repeat;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    margin-top: 9px;
    border-radius: 3px;
  }

  .setchart label, .setchart-nickname label {
    color: #FFF;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .setchart input, .setchart-nickname input, .setdetails, .setstats, .statform input.inputform {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .setchart input:hover, .setchart-nickname input:hover, .setdetails:hover, .setstats:hover, .statform input.inputform:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .setchart input:focus, .setchart-nickname input:focus, .setdetails:focus, .setstats:focus, .statform input.inputform:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .setdetails .detailcell {
    border-left: 1px solid #000;
  }

  .setchart-nickname {
    border-color: #181818;
    background: #181818;
    box-shadow: none;
    border-radius: 3px;
    border-top-left-radius: 0px;
  }

  .teambuilder-results .firstresult a {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
  }

  .teambuilder-results .firstresult a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.25) !important;
  }

  .teambuilder-results .result a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
  }

  .utilichart b {
    color: #000;
  }

  .utilichart {
    font-family: sans-serif;
  }

  .utilichart .numcol {
    color: #713624;
  }

  .utilichart .typecol img {
    opacity: 0.85;
  }

  .setstats .statrow-head em {
    color: #EEE;
  }

  .teambar button {
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    overflow-x: hidden;
    background: #181818;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
    border-color: #000;
  }

  .teambar button:hover {
    background: #303030;
    color: #F2F2F2;
    border-color: #000;
  }

  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: rgba(0, 0, 0, 0.75);
    opacity: 1;
    border-color: #000;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;    
  }

  .setmenu {
    top: 3px;
  }

  .setmenu button {
    font-family: sans-serif;
  }

  .setmenu button:hover {
    background: rgba(0, 0, 0, 0.4); 
    border-color: #000;
    color: #000;
    text-shadow: none;  
  }

  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #555;
    font-size: 10px;
  }

  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
    color: #555;
  }

  .utilichart .col {
    color: #222;
  }

  div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] {
    border: 1px solid #000;
    color: #FFF;
    background: #181818;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.2) inset, 0px 0px 0px rgba(228, 228, 228, 0.1) inset, 0px 0px 3px rgba(0, 0, 0, 0.5);
    font-family: sans-serif;
  }

  div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] .popupmenu button {
    color: #FFF;
    padding: 1px 4px;
    font-family: sans-serif;
  }

  div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] .popupmenu button:hover {
    border-color: #000;
    color: #FFF;
    background: #101010;
    padding: 0px 3px;
  }

  .statform {
    border-color: #000;
    background: #181818;
    border: 1px solid #000;
    margin-top: 15px;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    border-radius: 3px;
    border-top-left-radius: 0px;
    font-family: sans-serif;
  }

  .statform button {
    background: rgba(0, 0, 0, 0.5);
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 3px;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .statform button:hover {
    color: #0094FF;
  }

  .jslider .jslider-value {
    border: 1px solid #000;
    background: #151515;
  }

  .jslider .jslider-label {
    color: #FFF;
    opacity: 0.75;
  }

  button[name="back"], button[name="import"], .majorbutton, button[name="saveImport"], button[name="undeleteSet"], button[name="savePokemonImport"], button[name="closePokemonImport"], button[name="backup"], button[name="saveBackup"], button[name="validate"], .teampane .button, .format-select .select, .format-select .button, .teamchart .button {
    background: #181818;
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 3px;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }
  
  .teampane .button, .format-select .button, .teamchart .button {
    box-shadow: 0 -1px 2px #000 inset;
    padding: 5px 9px;
  }
  
  .format-select .select {
    box-shadow: none;
    border-top-left-radius: 0px;
  }

  button[name="back"]:hover, button[name="import"]:hover, .majorbutton:hover, .majorbutton:focus, button[name="saveImport"]:hover, button[name="saveImport"]:focus, button[name="undeleteSet"]:hover, button[name="undeleteSet"]:focus, button[name="savePokemonImport"]:hover, button[name="savePokemonImport"]:focus, button[name="closePokemonImport"]:hover, button[name="closePokemonImport"]:focus, button[name="backup"]:hover, button[name="backup"]:focus, button[name="saveBackup"]:hover, button[name="saveBackup"]:focus, button[name="validate"]:hover, button[name="validate"]:focus, .teampane .button:hover, .teampane .button:focus, .format-select .select:hover, .format-select .select:focus, .format-select .button:hover, .format-select .button:focus, .teamchart .button:hover, .teamchart .button:focus {
    color: #0094FF;
  }

  div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup {
    background: #333;
    border: 1px solid #000;
    color: #EEE;
    box-shadow: inset 1px 1px rgba(255, 255, 255, 0.3), inset -1px -1px rgba(0, 0, 0, 0.2), 2px 2px 3px rgba(0, 0, 0, .2);
  }
  
  div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup .popupmenu button {
    color: #DDD;
  }
  
  div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup .popupmenu button:hover, div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup .popupmenu button.sel:hover {
    background: rgba(0, 0, 0, 0.3);
    border-color: #000;
    color: #DDD;
  }
  
  div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup .popupmenu button.sel {
    border-color: #222;
  }
  
  div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup .popupmenu h3 {
    color: #0094FF;
    text-shadow: 0px 0px 2px #000;
  }

  .textbox.teamnameedit {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .textbox.teamnameedit:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .textbox.teamnameedit:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .detailsform {
    border-color: #000;
    background: #181818;
    border: 1px solid #000;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    border-radius: 3px;
    border-top-left-radius: 0px;
    padding: 15px 0px;
    margin: 15px 15px 0px 15px;
    font-family: sans-serif;
  }

  .detailsform .textbox {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
    font-family: sans-serif;
  }

  .detailsform .textbox:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .detailsform .textbox:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .teamchart li.format-select label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat;
    padding: 4px;
    color: #FFF;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  select[name="format"] {
    border: 1px solid rgba(0, 0, 0, 0.005);
    background: rgba(0, 0, 0, 0.43);
    font-family: sans-serif;
  }

  select[name="nature"] {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.43);
    font-family: sans-serif;
    color: #F2F2F2;
  }

  .setchart input.incomplete {
    color: #0094FF;
    border-color: #0094FF;
  }

  .teambuilder-clipboard-container {
    border-color: #000;
    border-radius: 3px;
    background: rgba(0, 0, 0, 0.43);
    height: 58px;
    margin-bottom: 5px;
    font-family: sans-serif;
  }

  .teambuilder-clipboard-button-right, .teambuilder-clipboard-button-left {
    background: #181818;
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 3px;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
    margin-top: 5px;   
    font-family: sans-serif;
  }

  .teambuilder-clipboard-button-right:hover, .teambuilder-clipboard-button-left:hover, .teambuilder-clipboard-button-right:focus, .teambuilder-clipboard-button-left:focus {
    color: #0094FF;
    text-shadow: 1px 1px #000;
    order-radius: 3px;
  }

  .teambuilder-clipboard-data {
    background: #181818;
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 3px;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .teambuilder-clipboard-data:hover {
    background: #181818;
    color: #0094FF;
    border: 1px solid #000;
    border-radius: 3px;
    text-shadow: 1px 1px #000;
  }

  .teambuilder-clipboard-data .section {
    border-right-color: #000;
  }

  .teamlist .team {
    background: #181818;
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 3px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    font-family: sans-serif;
  }

  .teamlist .team:hover {
    color: #0094FF;
  }
  
  .teamlist .team .pokemonicon span {
    color: transparent;
  }
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button {
    border-color: #000;
    background: #181818;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    border-radius: 3px;
  }
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover {
    border-color: #000;
    background: #181818;
    color: #0094FF;
  }
  
  .statform p.suggested a {
    color: #0094FF;
  }
  
  .folderpane {
    border-color: #333;
  }
  
  .folderlistafter::before, .folderlistbefore::before, .folderpane h3, .folder .selectFolder, .folderlist .foldersep::before {
    background: #333;
    border-color: #000;
  }
  
  .folderpane h3 {
    color: #0094FF;
    text-shadow: 0px 0px 2px black;
  }
  
  .folder .selectFolder {
    color: #DDD;
  }
  
  .folder .selectFolder:hover {
    background: #181818;
    color: #FFF;
  }
  
  .folder.cur .selectFolder, .folder.cur .selectFolder:hover, .folder.cur .selectFolder:active {
    border-color: #000;
  }

  .utilichart .sortrow {
    background: #181818;
    border-color: black !important;
  }
  
  .utilichart .sortcol {
    border: none !important;
    border-radius: 0px !important;
    padding: 0px !important;
  }
  
  .utilichart .sortcol.cur {
    background: black !important;
  }

  .jslider .jslider-value {
    color: white;
  }
}
