style.css:
@-moz-document url-prefix("http://forums.parallax.com/") {
    
    #Frame .Top{
        display:none;
    }
    
    #Frame .Banner{
        display: none;
    }
}

@-moz-document regexp('http://forums.parallax.com/(?!profile).*'){

    #Panel{
       display:none;
    }

    #Frame #Body .Row{
        max-width:100%;
    }
    #Frame .Row{
        max-width:100%;
    }
    #Body .ContentColumn{
       margin: 0px 0px 0px 0px;
    }
    
}
