-ms-filter: invert(1) hue-rotate(-5deg) !important;
-o-filter: invert(1) hue-rotate(-5deg) !important;
filter: invert(1) hue-rotate(-5deg) !important;
}
td.postLeft > *:not(.postKudosu):nth-child(5n) img,
td.postLeft > *:not(.postKudosu):nth-child(6n) img{display:none!important}
td.postLeft > *:not(.postKudosu):nth-child(5n),
td.postLeft > *:not(.postKudosu):nth-child(6n){width: 80px !important;height: 12px !important;margin-left: calc(50% - 40px) !important;}
td.postLeft > *:not(.postKudosu):nth-child(5n){
background: url("http://i.imgur.com/uCTliKr.png") !important;  margin-bottom: 2px !important;
}
td.postLeft > *:not(.postKudosu):nth-child(6n){
background: url("https://s.ppy.sh/images/nominator.png") !important;  height: 15px !important;
}
.postKudosu .boldgreen{color: #fff !important}
#quickreply-text{padding: 8px !important;width:calc(100% - 18px) !important}
#checkoutarea *{
background: transparent !important;
}
.content .paddingboth .beatmapListing{
border-left: 1px solid #FFF !important;
border-right: 1px solid #FFF !important;
border-bottom: 1px solid #FFF !important;
}
.pc-display{
color:  #ff3366 !important;
text-shadow: #ff0099 0px 0px 10px !important;
}
.report-popup{
box-shadow: 0px 0px 10px #FFF !important;
background: rgba(0, 0, 0, 0.4) none repeat scroll 0% 0% !important;
background: black !important;
}
.report-popup textarea{
border: 1px solid white !important;
}
.clear *{
background: none!important;
}
.modIconGroup {display: inline-block; margin: 2px;}
.modIcon {overflow: hidden; position: relative; width: 40px; height: 40px;}
.modIconOption, .modIconOption img, .modIcon img { width: 100%; height: 100%; }
.modIconOption {overflow: hidden; position: absolute; transform: skewX(-45deg);}
.modIconOption:first-child {left: 0px; transform-origin: 100% 0;}
.modIconOption:last-child {right: 0px; transform-origin: 0 100%;}
.modIconOption img {transform: skewX(45deg) !important; transform-origin: inherit!important;}
.notSelected {border: 3px solid transparent !important;}
.isSelected {border: 3px solid white!important;}
.partialSelected {border: 3px dashed white!important;}
.osupreview {width: 425px; height: 344px;}
.count-display {text-align: right;}
#opslider {width: 250px;}
.recentscore:hover {background: rgba(255,255,255,0.15)!important;}
.recentscore{background-color: rgba(255,255,255,0.15)!important;}
#leader .recentscore tbody{box-shadow: 0 0 0 5px rgba(255,255,255,0.15) !important;}
@-webkit-keyframes mentioned-pulse {
    0% {
    box-shadow: 0 0 0 0 #E5E4E2;
    }
    70% {
    box-shadow: 0 0 10px 10px rgba(229, 228, 226, 0)
    }
    to {
    box-shadow: 0 0 0 0 rgba(229, 228, 226, 0)
    }}
@keyframes mentioned-pulse {
    0% {
    box-shadow: 0 0 0 0 #E5E4E2;
    }
    70% {
    box-shadow: 0 0 10px 10px rgba(229, 228, 226, 0)
    }
    to {
    box-shadow: 0 0 0 0 rgba(229, 228, 226, 0)
    }}
.new-forum-preview{
margin: 0px auto !important;
width: 38% !important;
background: rgba(0,0,0,.4) !important;
border: 1px solid #FFF !important;
border-radius: 12px !important;
margin-top: 5px !important;
margin-bottom: 5px !important;
animation: mentioned-pulse 1.5s infinite;
}
#achievements .clear:last-child{
border: 1px solid #FFF !important;
background: rgba(0,0,0,.4) !important;
border-radius: 8px !important;
width: 450px !important;
margin-left: auto;
margin-right: auto;
animation: mentioned-pulse 1.5s infinite !important;
}
#full table tbody tr td:nth-child(2) b, #truncated table tbody tr td:nth-child(2) b{
color: rgb(218,185,52) !important;
text-shadow: rgb(218,175,42) 0px 0px 6px !important;
}
#full table tbody tr td:nth-child(2) a, #truncated table tbody tr td:nth-child(2) a{
text-shadow: #FFF 0px 0px 6px !important;
}
#chart1{
bottom: 10px !important;
padding-bottom: 0px !important;
}
