style.css:
@-moz-document regexp("https?://(\.+)\\.service-now\\.com/.*") {
  
 
/*                       ##                ####                       ##    
//                       ##               ##                          ##    
//                       ##               ##                          ##    
//    #####    #####   ######            #####     #####   ## ###   ######  
//   ##       ##   ##    ##               ##      ##   ##  ###  ##    ##    
//    ####    #######    ##               ##      ##   ##  ##   ##    ##    
//       ##   ##         ##               ##      ##   ##  ##   ##    ##    
//   #####     #####      ###             ##       #####   ##   ##     ###  
//                                        #                                 
*/                                                                          
   
  
   * {
    text-rendering: optimizelegibility;
  }
  
  /*[[sidebar]]*/

/*    ##                                                   
//    ##                                                   
//    ##                                                   
//    ######    ######  ## ###   ## ###    #####   ## ###  
//    ##   ##  ##   ##  ###  ##  ###  ##  ##   ##  ###     
//    ##   ##  ##   ##  ##   ##  ##   ##  #######  ##      
//    ##   ##  ##  ###  ##   ##  ##   ##  ##       ##      
//    ######    ### ##  ##   ##  ##   ##   #####   ##      
//                                                         
*/                                                         
  
  
  .header_links {
    display: none;
  }
  
/*     ###                               
//      ##                               
//      ##                               
//      ##      #####    ######   #####  
//      ##     ##   ##  ##   ##  ##   ## 
//      ##     ##   ##  ##   ##  ##   ## 
//      ##     ##   ##  ##   ##  ##   ## 
//     ####     #####    ######   #####  
//                           ##          
//                       #####           
*/  
  
  #mainBannerImage {
    display: none;
  }
  
/*     ###                                              ##             ##             
//      ##                                              ##             ##             
//      ##                                              ##                            
//      ##      #####    ######   ######   #####    ######           ####     ## ###  
//      ##     ##   ##  ##   ##  ##   ##  ##   ##  ##   ##             ##     ###  ## 
//      ##     ##   ##  ##   ##  ##   ##  #######  ##   ##             ##     ##   ## 
//      ##     ##   ##  ##   ##  ##   ##  ##       ##   ##             ##     ##   ## 
//     ####     #####    ######   ######   #####    ######           ######   ##   ## 
//                           ##       ##                                              
//                       #####    #####                                               
*/
  
  #gsft_login {
    display: block !important;
    position: absolute !important;
    top: 9px !important;
  }
  
  #gsft_login > span:nth-child(1) {
    display: none;
  }
  
  #gsft_login > span:nth-child(2):before {
  content: "/*[[loginBefore]]*/ :: "; 
  }
  
  #loggedin {
    height: 35px !important;
    line-height: 25px !important;
    
  }
  
/*     ###                                           ##    
//      ##                                           ##    
//      ##                                           ##    
//      ##      #####    ######   #####   ##   ##  ######  
//      ##     ##   ##  ##   ##  ##   ##  ##   ##    ##    
//      ##     ##   ##  ##   ##  ##   ##  ##   ##    ##    
//      ##     ##   ##  ##   ##  ##   ##  ##  ###    ##    
//     ####     #####    ######   #####    ### ##     ###  
//                           ##                            
//                       #####                             
*/
  
  #gsft_logout {
    position: absolute;
    top: 5px;
    right: 5px;
  }

/*                        ##       ##       ##                               
//                        ##       ##       ##                               
//                        ##       ##                                        
//     #####    #####   ######   ######   ####     ## ###    ######   #####  
//    ##       ##   ##    ##       ##       ##     ###  ##  ##   ##  ##      
//     ####    #######    ##       ##       ##     ##   ##  ##   ##   ####   
//        ##   ##         ##       ##       ##     ##   ##  ##   ##      ##  
//    #####     #####      ###      ###   ######   ##   ##   ######  #####   
//                                                               ##          
//                                                           #####           
*/
  
  #navpage_header_control_button {
    position: absolute;
    top: 0px;
    right: 6.5rem;
  }
  
  #navpage_header_control_button::before {
    font-size: 125% !important;
  }
/*                                                 ##       ##                        
//                                                 ##       ##                        
//                                                 ##       ##                        
//     #####    #####    ######  ## ###    #####   ######   ######    #####   ##  ##  
//    ##       ##   ##  ##   ##  ###      ##       ##   ##  ##   ##  ##   ##   ####   
//     ####    #######  ##   ##  ##       ##       ##   ##  ##   ##  ##   ##    ##    
//        ##   ##       ##  ###  ##       ##       ##   ##  ##   ##  ##   ##   ####   
//    #####     #####    ### ##  ##        #####   ##   ##  ######    #####   ##  ##  
//                                                                                    
*/                                

  #banner_row + tr {
    height: 0 !important;
  }
  
  #banner_top_right {
    display: block !important;
    position: absolute;
    width: 150px;
    top: 3.5px;
    right: 11.5rem;
  }
  
  .input-group-addon-transparent::before {
    position: relative;
    top: -3px;
  }
  
  .input-group-addon-pull-right::before {
    position: relative;
    top: 0px;
  }
  
  .rightButtons {
    display: none;
  }
  
/*      ##                         ##          ##                      ##               ###       ##                ##    
//      ##                         ##          ##                      ##                ##       ##                ##    
//                                             ##                      ##                ##                         ##    
//    ####     ## ###    #####   ####      ######   #####   ## ###   ######              ##     ####      #####   ######  
//      ##     ###  ##  ##         ##     ##   ##  ##   ##  ###  ##    ##                ##       ##     ##         ##    
//      ##     ##   ##  ##         ##     ##   ##  #######  ##   ##    ##                ##       ##      ####      ##    
//      ##     ##   ##  ##         ##     ##   ##  ##       ##   ##    ##                ##       ##         ##     ##    
//    ######   ##   ##   #####   ######    ######   #####   ##   ##     ###             ####    ######   #####       ###  
//                                                                                                                        
*/                                                                                                                       
  
  
  thead * {
    background-color: /*[[theadColor]]*/ !important;
  }
  
  .list_row td
  {
    border-style: solid !important;
    border-color: #aaa !important;
    border-width: 1px 0 !important;
  }

  
  .list_even td,
  .list_even .datex,
  .list_even .list2_cell_image {
    background-color: /*[[evenColor]]*/ !important;
  }


  .list_div tr.hover td,
  .list_div tr.hover td .datex,
  .list_div tr.hover td .list2_cell_image {
    background-color: /*[[hoverColor]]*/ !important;
  }


  .list_header > th > span > img {
    display: none;
  }
  
/*                                                                           
//                                                                           
//                                                                           
//    ## ###    ######  ### ##            ### ##    #####   ## ###   ##   ## 
//    ###  ##  ##   ##   ## #             ## # ##  ##   ##  ###  ##  ##   ## 
//    ##   ##  ##   ##   ## #             ## # ##  #######  ##   ##  ##   ## 
//    ##   ##  ##  ###    ##              ## # ##  ##       ##   ##  ##  ### 
//    ##   ##   ### ##    ##              ##   ##   #####   ##   ##   ### ## 
//                                                                           
*/                                                                             


  .navbar-default {
    background-color: #fff !important;
    border: none !important;
  }

  /*[[filters]]*/

  .navigator-filter-form {
    margin-top: 1px !important;
  }
  
  .navbar {
    box-shadow: none !important;
  }
  
  .nav-wrapper {
    color: #666 !important;
    padding-right: .5rem !important;
  }

  #nav_west_center {
    background-color: #fff !important;
  }
  
  #navigator_favorite_switcher:before {
    color: #999 !important;
  }
    
  .nav_menu {
    background-color: transparent !important;
    color: #666 !important;
    font-weight: 700 !important;
  }

    /*[[labels]]*/

  .nav-wrapper  *
  {
    font-size: 1em;
    line-height: 1.75em;
    color: #666 !important;
  }
  
  .nav-wrapper .icon:before {
    font-size: .5em;
    position: relative;
    top: -1px;
    color: #999;
  }

    /*[[stars]]*/

}

