style.css:
/* Custom Weasyl: Grid view thumbnails - by Storm */
/* https://www.weasyl.com/~storm */

@-moz-document domain("weasyl.com") {

ul.thumbnail-grid
  {
  padding: 0px 25px 0px 25px !important;
  }
ul.thumbnail-grid li
  {
  width: 300px !important;
  margin: 0px 15px 10px 0px !important;
  float: left;
  }
ul.thumbnail-grid li figure.thumb a
  {
  width: 300px !important;
  height: 240px !important;
  }

}
