/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::: SETTINGS ::::::::::::::::::::::::::::::::::::::::::::::::::::::: */
:root {
  --main-nav-bg-color: /*[[bg_color]]*/;
  --main-url-bg-color: /*[[bg_color]]*/;
  --main-url-text-color: /*[[text_color]]*/;

  --main-tab-active-bg-color: /*[[bg_color]]*/;
  --main-tab-active-hover-bg-color: /*[[bg_color]]*/;
  --main-tab-active-text-color: /*[[text_color]]*/;
  
  --main-tab-inactive-bg-color: /*[[itbg_color]]*/;
  --main-tab-inactive-hover-bg-color: /*[[ithbg_color]]*/;
  --main-tab-inactive-text-color: /*[[ittxt_color]]*/;
  
  --main-border-radius: 0px;
  --main-theme-border-color: rgba(0, 0, 0,.2);
  --main-page-border-color: rgba(0, 0, 0,.2);

  --main-font-family: "/*[[font]]*/", sans-serif;
  --main-theme-font-size: /*[[tfontz]]*/;
}
  
/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::: THEME ::::::::::::::::::::::::::::::::::::::::::::::::::::::: */
  
/* TABS BACKGROUND SETTINGS */

#main-window[sizemode="normal"]:not([inFullscreen]) #TabsToolbar {
	background: transparent !important;
	/*margin-right: -32px !important;
	margin-left: -16px !important;*/
}
  
#main-window[sizemode="maximized"] #TabsToolbar {
	background: transparent !important;
	margin-top: 1px !important;
	/*margin-right: -32px !important;
	margin-left: -16px !important;*/
}
  
.scrollbutton-up, .scrollbutton-down {
	/*display: none!important;*/
}

#TabsToolbar:not(:-moz-lwtheme)::after {
  display: none;
}

/* TABS */
#TabsToolbar .tabbrowser-tab[selected] {
  font-weight: normal !important;
  border: 1px solid var(--main-theme-border-color) !important;
  border-bottom: 0px solid transparent!important;
  border-radius: 0px !important;
  background: var(--main-tab-active-bg-color) !important;
  color: var(--main-tab-active-text-color) !important;
  transition-property: background, color;
  transition-duration: 0ms;
}

