/* DISABLE AUSTRALIS TAB */
#TabsToolbar .tab-background {
  background: none;
}

#TabsToolbar .tab-background-start::before,  #TabsToolbar .tab-background-end::before {
  background-image: none !important;
}

.tab-background-start[selected=true]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
  background-image: none!important;
}

#TabsToolbar .tab-background-middle {
  background: transparent !important;
}

#TabsToolbar .tabbrowser-tab:after,  #TabsToolbar .tabbrowser-tab:before {
  display: none !important;
}

/* NAVIGATION BAR/ADDRESS BAR/SEARCH BAR SETTING */
#personal-bookmarks {
  /*margin-bottom: 1px!important;*/
}

#TabsToolbar:not(:-moz-lwtheme)::after {
  display: none;
}

#navigator-toolbox:after {
  height: 0px !important;
}

/* NAV-BAR */
#nav-bar {
  background: var(--main-nav-bg-color) !important;
  box-shadow: none !important;
  border-color: var(--main-theme-border-color) !important;
  border: 1px solid var(--main-theme-border-color) !important;
  /*border-bottom: 1px solid var(--main-theme-border-color)!important;*/
  margin-top: 0px !important;
  color: var(--main-url-text-color) !important;
}

#main-window[sizemode="maximized"] #nav-bar {
 border-radius: 0px !important;
}

#main-window[sizemode="normal"]:not([inFullscreen]) #nav-bar {
  border-radius: 0px !important;
}

/* BACK BUTTON */
#back-button .toolbarbutton-icon {
  /*
  list-style-image: none !important;
  background: url(data:image/png;base64,) center no-repeat !important;
  */
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper > #back-button[disabled] .toolbarbutton-icon,
window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #back-button[disabled] .toolbarbutton-icon {
  /*
  opacity: .3!important;
  */
}

/* FORWARD BUTTON */
#forward-button {
  /*
  padding-left: 10px !important;
  */
}

#forward-button .toolbarbutton-icon {
  border-radius: 0 25px 25px 0!important;
  /*
  list-style-image: none !important;
  background: url(data:image/png;base64,) center no-repeat !important;
  */
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper > #forward-button[disabled],
window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #forward-button[disabled],
#forward-button:not([disabled]) {
  /*margin-left: 0px!important;*/
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper > #forward-button[disabled] .toolbarbutton-icon,
window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #forward-button[disabled] .toolbarbutton-icon {
  /*
  opacity: .3!important;
  */
}
  
/* URL BAR */
#urlbar {
  margin-left: 7px !important;
  background: var(--main-url-bg-color) !important;
  box-shadow: none !important;
  border-color: var(--main-url-bg-color) !important;
  /*border-left: 1px solid var(--main-theme-border-color)!important;*/
  border-right: 1px solid var(--main-theme-border-color)!important;
  border-radius: 0px !important;
  color: var(--main-url-text-color)!important;
  font-family: var(--main-font-family) !important;
  font-size: var(--main-theme-font-size) !important;
}

/* IDENTITY */
#identity-box, #identity-icon-labels{
  background: transparent !important;
}

/* .bad-content-blocked */
#notification-popup-box {
  -moz-appearance: none !important;
  background: transparent !important;
  background-image: none!important;
  box-shadow: none !important;
  border-image: none !important;
  border: 0px;
  list-style-image: none;
}

/* SEARCH BAR */
#searchbar .searchbar-textbox {
  /*min-height: 38px!important;*/
  margin-left: -7px !important;
  background: var(--main-url-bg-color) !important;
  box-shadow: none !important;
  border-color: var(--main-url-bg-color) !important;
  border-left: 1px solid var(--main-theme-border-color)!important;
  border-right: 1px solid var(--main-theme-border-color)!important;
  border-radius: 0px !important;
  color: var(--main-url-text-color)!important;
  font-family: var(--main-font-family) !important;
  font-size: var(--main-theme-font-size) !important;
}

/* BOOKMARKS  */
#PersonalToolbar { 
  background: var(--main-nav-bg-color) !important;
  border: 1px solid var(--main-theme-border-color) !important;
  border-top: 0px !important;
}
#PersonalToolbar .toolbarbutton-text {
  color: var(--main-url-text-color) !important;
}

/* TABS CLOSE BUTTON */
.tabs-closebutton-box, .tab-close-button {
  filter: grayscale(1) contrast(200%) invert(1);
}

.tabs-closebutton-box:hover, .tab-close-button:hover {
  filter: grayscale(1) contrast(200%) invert(0);
}

/* MENU BUTTON */
#PanelUI-menu-button {
  border-left: 1px solid var(--main-theme-border-color)!important;
}

#PanelUI-button {
  background: rgba(0,0,0,.15) !important;
  border-image: none !important;
  border: rgba(0,0,0,.15) !important;
}

/* HIDES PLUGINS NOTIFICATION ICON THAT MAY CHANGE ADDRESS BAR'S HEIGHT */
#plugins-notification-icon {
	display: none !important;
}

/* REMOVES VS SEPARATOR */
#browser-bottombox {
	border: none !important;
}

/* BORDERLESS*/
#browser-border-end, #browser-border-start {
  background:  transparent !important;
	border-left: 1px solid var(--main-page-border-color)!important;
}

#browser-bottombox {
  background:  transparent !important;
  border-bottom: 1px solid var(--main-page-border-color)!important;
}
  
#browser-border-end, #browser-border-start {
	/*display: none !important;*/
}

/* BORDERLESS EFFECT FOR BORDERLESS VS */
#main-window {
	-moz-appearance: -moz-win-borderless-glass !important;
	-moz-appearance: -moz-win-glass !important;
}
